import React from 'react';
import './Sidebar.css';

const Sidebar = ({ collapsed, onToggle, onLogout, user }) => {
  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">‚ö°</div>
          {!collapsed && <span className="logo-text">ZMO Admin</span>}
        </div>
        <button className="toggle-btn" onClick={onToggle}>
          {collapsed ? '‚Üí' : '‚Üê'}
        </button>
      </div>

      <nav className="sidebar-nav">
        <a href="/dashboard" className="nav-item">
          <span className="nav-icon">üìä</span>
          {!collapsed && <span>Dashboard</span>}
        </a>
        <a href="/blogs" className="nav-item">
          <span className="nav-icon">üìù</span>
          {!collapsed && <span>Blogs</span>}
        </a>
        <a href="/projects" className="nav-item">
          <span className="nav-icon">üöÄ</span>
          {!collapsed && <span>Projects</span>}
        </a>
      </nav>

      <div className="sidebar-footer">
        <div className="user-info">
          <div className="user-avatar">üë§</div>
          {!collapsed && (
            <div className="user-details">
              <span className="username">{user?.username || 'Admin'}</span>
              <span className="user-role">{user?.role || 'Administrator'}</span>
            </div>
          )}
        </div>
        <button className="logout-btn" onClick={onLogout}>
          <span className="logout-icon">üö™</span>
          {!collapsed && <span>Logout</span>}
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
