// admin-panel/src/components/dashboard/BlogsManager.js
import React, { useState, useEffect } from 'react';
import BlogForm from '../forms/BlogForm';
import { blogAPI } from '../../services/api';
const BlogsManager = () => {
 

  const [editingBlog, setEditingBlog] = useState(null); // blog being edited
  const [loading, setLoading] = useState(false);

  // Fetch blogs from API
  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const data = await blogAPI.getAll();
      setBlogs(data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching blogs:', err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, []);

  // Open form for new blog
  const handleAddBlog = () => {
    setEditingBlog(null);
    setShowForm(true);
  };

  // Open form for editing existing blog
  const handleEditBlog = (blog) => {
    setEditingBlog(blog);
    setShowForm(true);
  };

  // Delete a blog
  const handleDeleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await blogAPI.delete(id);
        setBlogs(blogs.filter((b) => b.id !== id));
      } catch (err) {
        console.error('Error deleting blog:', err);
      }
    }
  };

  // Handle form submit for create/update
  const handleFormSubmit = async (formData) => {
    try {
      if (editingBlog) {
        const updated = await blogAPI.update(editingBlog.id, formData);
        setBlogs(blogs.map((b) => (b.id === updated.id ? updated : b)));
      } else {
        const created = await blogAPI.create(formData);
        setBlogs([created, ...blogs]);
      }
      setShowForm(false);
      setEditingBlog(null);
    } catch (err) {
      console.error('Error saving blog:', err);
    }
  };

  return (
    <div>
      <h2>Blog Management</h2>
      <button onClick={handleAddBlog}>
        {showForm ? 'Cancel' : 'Add New Blog'}
      </button>

      {showForm && (
        <BlogForm
          blog={editingBlog}
          onSubmit={handleFormSubmit}
          onCancel={() => {
            setShowForm(false);
            setEditingBlog(null);
          }}
        />
      )}

      {loading ? (
        <p>Loading blogs...</p>
      ) : blogs.length === 0 ? (
        <p>No blogs found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {blogs.map((blog) => (
              <tr key={blog.id}>
                <td>{blog.title}</td>
                <td>
                  <button onClick={() => handleEditBlog(blog)}>Edit</button>
                  <button onClick={() => handleDeleteBlog(blog.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BlogsManager;
