// admin-panel/src/services/api.js - FIXED VERSION
import axios from 'axios';

// ==========================================
// üöÄ FIXED ENVIRONMENT CONFIGURATION
// ==========================================
const API_BASE_URL = 'https://zmo-backend.onrender.com/api'; // ‚úÖ DIRECT URL
const ENVIRONMENT = process.env.NODE_ENV || 'production';
const API_TIMEOUT = 30000;

console.log('üîß ZMO Admin Panel API Configuration:', {
  apiBaseURL: API_BASE_URL,
  environment: ENVIRONMENT,
  timeout: API_TIMEOUT
});

// ==========================================
// üõ†Ô∏è AXIOS INSTANCE CONFIGURATION - SIMPLIFIED
// ==========================================
const api = axios.create({
  baseURL: API_BASE_URL, // ‚úÖ Use direct URL
  timeout: API_TIMEOUT,
  headers: { 
    'Content-Type': 'application/json',
  },
});

// Keep the rest of your interceptors and functions the same...
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken') || sessionStorage.getItem('adminToken');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    console.log(`üöÄ API Request: ${config.method?.toUpperCase()} ${config.baseURL}${config.url}`);
    return config;
  },
  (error) => {
    console.error('‚ùå Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ API Response: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('‚ùå API Error:', {
      url: error.config?.url,
      method: error.config?.method,
      status: error.response?.status,
      message: error.message,
      code: error.code
    });
    
    if (error.code === 'ECONNABORTED') {
      error.message = 'Request timeout - backend might be starting up';
    } else if (!error.response) {
      error.message = `Cannot connect to server at ${API_BASE_URL}`;
    }
    
    return Promise.reject(error);
  }
);

// ==========================================
// üîê AUTHENTICATION API - SIMPLIFIED
// ==========================================
export const authAPI = {
  login: async (email, password, rememberMe = false) => {
    try {
      console.log('üîê Attempting login to:', `${API_BASE_URL}/auth/login`);
      
      const response = await api.post('/auth/login', { 
        email: email.trim().toLowerCase(), 
        password: password.trim() 
      });
      
      const { success, token, user, message } = response.data;
      
      if (success && token) {
        // Store token
        if (rememberMe) {
          localStorage.setItem('adminToken', token);
        } else {
          sessionStorage.setItem('adminToken', token);
        }
        localStorage.setItem('adminUser', JSON.stringify(user));
        
        console.log('‚úÖ Login successful', { user: user?.name });
        return { success: true, user, token, message };
      } else {
        throw new Error(message || 'Login failed');
      }
    } catch (error) {
      console.error('‚ùå Login failed:', error.message);
      throw error;
    }
  }
};

// Test function to verify connection
export const testConnection = async () => {
  try {
    const response = await axios.get('https://zmo-backend.onrender.com/api/health');
    console.log('‚úÖ Backend test successful:', response.data);
    return true;
  } catch (error) {
    console.error('‚ùå Backend test failed:', error.message);
    return false;
  }
};
