import React, { useState, useEffect } from 'react';
import { checkBackendConnection } from '../services/api';

const BackendTester = () => {
  const [status, setStatus] = useState('testing');
  const [message, setMessage] = useState('');
  const [url, setUrl] = useState('');

  const testConnection = async (testUrl = null) => {
    setStatus('testing');
    setMessage(`Testing connection to ${testUrl || 'default URL'}...`);
    
    try {
      // If a specific URL is provided, test it directly
      if (testUrl) {
        const response = await axios.get(`${testUrl}/api/health`);
        setStatus('connected');
        setMessage(`‚úÖ Connected to ${testUrl}`);
        setUrl(testUrl);
      } else {
        const response = await checkBackendConnection();
        setStatus('connected');
        setMessage(`‚úÖ Backend is running!`);
        setUrl('default');
      }
    } catch (error) {
      setStatus('error');
      setMessage(`‚ùå Cannot connect: ${error.message}`);
    }
  };

  useEffect(() => {
    testConnection();
  }, []);

  const testDifferentUrls = async () => {
    const testUrls = [
      'http://localhost:5000',
      'http://127.0.0.1:5000', 
      'http://192.168.100.62:5000',
      'http://0.0.0.0:5000'
    ];

    for (let testUrl of testUrls) {
      setMessage(`Testing ${testUrl}...`);
      try {
        const response = await axios.get(`${testUrl}/api/health`);
        setStatus('connected');
        setMessage(`‚úÖ Found backend at: ${testUrl}`);
        setUrl(testUrl);
        return;
      } catch (error) {
        console.log(`‚ùå ${testUrl} failed: ${error.message}`);
      }
    }
    
    setStatus('error');
    setMessage('‚ùå Could not find backend on any common URL');
  };

  return (
    <div style={{ 
      padding: '20px', 
      border: '1px solid #ccc', 
      borderRadius: '8px',
      margin: '10px',
      backgroundColor: status === 'connected' ? '#d4edda' : 
                     status === 'error' ? '#f8d7da' : '#fff3cd'
    }}>
      <h3>üîß Backend Connection Test</h3>
      <p><strong>Status:</strong> {status.toUpperCase()}</p>
      <p><strong>Message:</strong> {message}</p>
      <p><strong>Current URL:</strong> {url || 'Not set'}</p>
      
      <div style={{ marginTop: '15px' }}>
        <button onClick={testConnection} style={{ marginRight: '10px' }}>
          Test Default URL
        </button>
        <button onClick={testDifferentUrls}>
          Test All Possible URLs
        </button>
      </div>

      {status === 'error' && (
        <div style={{ marginTop: '15px', padding: '10px', backgroundColor: '#f8d7da' }}>
          <h4>üö® Troubleshooting Steps:</h4>
          <ol>
            <li>Make sure backend is running: <code>cd backend && npm start</code></li>
            <li>Check if port 5000 is available</li>
            <li>Verify no firewall is blocking the connection</li>
            <li>Try restarting both frontend and backend</li>
          </ol>
        </div>
      )}
    </div>
  );
};

export default BackendTester;
