// admin-panel/src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL;

console.log('ðŸ”§ API Base URL:', API_BASE_URL);

// Axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add auth token to all requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle unauthorized responses globally
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

/* -------------------------
   AUTHENTICATION API
------------------------- */
export const authAPI = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  },

  logout: async () => {
    const response = await api.post('/auth/logout');
    return response.data;
  },

  getCurrentUser: async () => {
    const response = await api.get('/auth/verify');
    return response.data;
  }
};

/* -------------------------
   DASHBOARD API
------------------------- */
export const dashboardAPI = {
  getStats: async () => {
    const response = await api.get('/admin/dashboard/stats');
    return response.data;
  },

  getRecentActivity: async () => {
    const response = await api.get('/admin/dashboard/recent-activity');
    return response.data;
  },

  createTestBlog: async () => {
    const response = await api.post('/admin/dashboard/test-blog');
    return response.data;
  },

  createTestProject: async () => {
    const response = await api.post('/admin/dashboard/test-project');
    return response.data;
  },

  createTestMessage: async () => {
    const response = await api.post('/admin/dashboard/test-message');
    return response.data;
  },

  publishBlog: async (blogId) => {
    const response = await api.post(`/admin/blogs/${blogId}/publish`);
    return response.data;
  }
};

/* -------------------------
   BLOG API
------------------------- */
export const blogAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/blogs?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/blogs/${id}`);
    return response.data;
  },

  create: async (data) => {
    const response = await api.post('/admin/blogs', data);
    return response.data;
  },

  update: async (id, data) => {
    const response = await api.put(`/admin/blogs/${id}`, data);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/blogs/${id}`);
    return response.data;
  }
};

/* -------------------------
   PROJECT API
------------------------- */
export const projectAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/projects?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/projects/${id}`);
    return response.data;
  },

  create: async (data) => {
    const response = await api.post('/admin/projects', data);
    return response.data;
  },

  update: async (id, data) => {
    const response = await api.put(`/admin/projects/${id}`, data);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/projects/${id}`);
    return response.data;
  }
};

/* -------------------------
   CONTACT API
------------------------- */
export const contactAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/contacts?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/contacts/${id}`);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/contacts/${id}`);
    return response.data;
  }
};

/* -------------------------
   HEALTH API
------------------------- */
export const healthAPI = {
  getStatus: async () => {
    const response = await api.get('/admin/health');
    return response.data;
  }
};

export default api;
