import React, { useState, useEffect } from 'react';
import { projectsAPI } from '../services/api';
import ProjectManager from '../components/ProjectManager';
import './ProjectsPage.css';

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const projectData = await projectsAPI.getAll();
      setProjects(projectData);
    } catch (err) {
      setError('Failed to fetch projects');
      console.error('Error fetching projects:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (projectData) => {
    try {
      const createdProject = await projectsAPI.create(projectData);
      setProjects(prev => [createdProject, ...prev]);
      setShowCreateForm(false);
      setError('');
    } catch (err) {
      setError('Failed to create project');
      console.error('Error creating project:', err);
    }
  };

  const handleUpdateProject = async (projectData) => {
    try {
      const updatedProject = await projectsAPI.update(projectData.id, projectData);
      setProjects(prev => prev.map(project => 
        project.id === projectData.id ? updatedProject : project
      ));
      setEditingProject(null);
      setError('');
    } catch (err) {
      setError('Failed to update project');
      console.error('Error updating project:', err);
    }
  };

  const handleDeleteProject = async (id) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        await projectsAPI.delete(id);
        setProjects(prev => prev.filter(project => project.id !== id));
        setError('');
      } catch (err) {
        setError('Failed to delete project');
        console.error('Error deleting project:', err);
      }
    }
  };

  if (loading) {
    return (
      <div className="projects-page">
        <div className="loading">Loading projects...</div>
      </div>
    );
  }

  return (
    <div className="projects-page">
      <div className="page-header">
        <h1>Project Management</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowCreateForm(true)}
        >
          + Add New Project
        </button>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>√ó</button>
        </div>
      )}

      {/* Project Manager Modal */}
      {showCreateForm && (
        <ProjectManager
          onSave={handleCreateProject}
          onCancel={() => setShowCreateForm(false)}
        />
      )}

      {editingProject && (
        <ProjectManager
          project={editingProject}
          onSave={handleUpdateProject}
          onCancel={() => setEditingProject(null)}
        />
      )}

      {/* Projects Grid */}
      <div className="projects-grid">
        {projects.length === 0 ? (
          <div className="empty-state">
            <p>No projects found. Add your first project!</p>
          </div>
        ) : (
          projects.map(project => (
            <div key={project.id} className={`project-card ${project.featured ? 'featured' : ''}`}>
              {/* Project Image */}
              {(project.image || project.imageUrl) && (
                <div className="project-image">
                  <img 
                    src={project.image || project.imageUrl} 
                    alt={project.name}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                  {project.featured && <span className="featured-badge">‚≠ê Featured</span>}
                </div>
              )}
              
              <div className="project-content">
                <div className="project-header">
                  <h3>{project.name}</h3>
                  <span className={`status ${project.status}`}>
                    {project.status}
                  </span>
                </div>
                
                <p className="project-description">{project.description}</p>
                
                {/* Technologies */}
                {project.technologies && project.technologies.length > 0 && (
                  <div className="project-technologies">
                    {project.technologies.map(tech => (
                      <span key={tech} className="tech-tag">{tech}</span>
                    ))}
                  </div>
                )}
                
                {/* Project Links */}
                <div className="project-links">
                  {project.demoUrl && (
                    <a 
                      href={project.demoUrl} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="project-link demo-link"
                    >
                      üåê Live Demo
                    </a>
                  )}
                  {project.githubUrl && (
                    <a 
                      href={project.githubUrl} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="project-link github-link"
                    >
                      üíª GitHub
                    </a>
                  )}
                </div>
                
                {/* Project Meta */}
                <div className="project-meta">
                  <span>Created: {new Date(project.createdAt).toLocaleDateString()}</span>
                </div>
                
                {/* Project Actions */}
                <div className="project-actions">
                  <button 
                    className="btn-edit"
                    onClick={() => setEditingProject(project)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn-danger"
                    onClick={() => handleDeleteProject(project.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProjectsPage;
