import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    { path: '/', label: 'Dashboard', icon: 'üìä' },
    { path: '/blogs', label: 'Blogs', icon: 'üìù' },
    { path: '/projects', label: 'Projects', icon: 'üíº' },
  ];

  return (
    <aside style={{
      width: '250px',
      background: '#2c3e50',
      color: 'white',
      height: '100vh',
      position: 'fixed',
      left: 0,
      top: 0
    }}>
      <div style={{ padding: '20px' }}>
        <h3 style={{ color: 'white', marginBottom: '30px' }}>Admin Panel</h3>
        <nav>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {menuItems.map(item => (
              <li key={item.path} style={{ marginBottom: '5px' }}>
                <Link
                  to={item.path}
                  style={{
                    display: 'block',
                    padding: '12px 15px',
                    color: location.pathname === item.path ? 'white' : '#bdc3c7',
                    background: location.pathname === item.path ? '#34495e' : 'transparent',
                    textDecoration: 'none',
                    borderRadius: '4px',
                    borderLeft: location.pathname === item.path ? '4px solid #3498db' : 'none'
                  }}
                >
                  <span style={{ marginRight: '10px' }}>{item.icon}</span>
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </aside>
  );
};

export default Sidebar;
