// admin-panel/src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('ðŸ”§ API Base URL:', API_BASE_URL); // Add this for debugging

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // Add timeout
});

// Add request interceptor for debugging
api.interceptors.request.use((config) => {
  console.log(`ðŸš€ Making ${config.method?.toUpperCase()} request to: ${config.baseURL}${config.url}`);
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
    console.log('ðŸ”‘ Token attached to request');
  }
  return config;
});

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… Response received: ${response.status}`, response.data);
    return response;
  },
  (error) => {
    console.error('âŒ API Error:', {
      message: error.message,
      status: error.response?.status,
      data: error.response?.data,
      url: error.config?.url
    });
    
    if (error.response?.status === 401) {
      console.log('ðŸ”’ Authentication failed, redirecting to login');
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Add a health check function
export const healthAPI = {
  check: async () => {
    try {
      const response = await api.get('/health');
      return response.data;
    } catch (error) {
      throw error;
    }
  }
};

// Authentication API
export const authAPI = {
  // Admin login
  login: async (email, password) => {
    const response = await api.post('/admin/auth/login', { email, password });
    return response.data;
  },

  // ... rest of your existing code
};
