import React, { useState, useEffect } from 'react';
import { blogsAPI } from '../services/api';
import BlogManager from '../components/BlogManager';
import './BlogsPage.css';

const BlogsPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const blogData = await blogsAPI.getAll();
      setBlogs(blogData);
    } catch (err) {
      console.error('Error fetching blogs:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (blogData) => {
    try {
      const createdBlog = await blogsAPI.create(blogData);
      setBlogs(prev => [createdBlog, ...prev]);
      setShowCreateForm(false);
    } catch (err) {
      console.error('Error creating blog:', err);
    }
  };

  const handleUpdateBlog = async (blogData) => {
    try {
      const updatedBlog = await blogsAPI.update(blogData.id, blogData);
      setBlogs(prev => prev.map(blog => 
        blog.id === blogData.id ? updatedBlog : blog
      ));
      setEditingBlog(null);
    } catch (err) {
      console.error('Error updating blog:', err);
    }
  };

  const handleDeleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await blogsAPI.delete(id);
        setBlogs(prev => prev.filter(blog => blog.id !== id));
      } catch (err) {
        console.error('Error deleting blog:', err);
      }
    }
  };

  const togglePublish = async (blog) => {
    try {
      const updatedBlog = await blogsAPI.update(blog.id, {
        ...blog,
        published: !blog.published
      });
      setBlogs(prev => prev.map(b => 
        b.id === blog.id ? updatedBlog : b
      ));
    } catch (err) {
      console.error('Error updating blog status:', err);
    }
  };

  if (loading) {
    return <div className="loading">Loading blogs...</div>;
  }

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Blog Management</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowCreateForm(true)}
        >
          + Create New Blog
        </button>
      </div>

      {/* Blog Manager Modal */}
      {showCreateForm && (
        <BlogManager
          onSave={handleCreateBlog}
          onCancel={() => setShowCreateForm(false)}
        />
      )}

      {editingBlog && (
        <BlogManager
          blog={editingBlog}
          onSave={handleUpdateBlog}
          onCancel={() => setEditingBlog(null)}
        />
      )}

      {/* Blogs Grid */}
      <div className="blogs-grid">
        {blogs.length === 0 ? (
          <div className="empty-state">
            <p>No blogs found. Create your first blog post!</p>
          </div>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="blog-card">
              {blog.image && (
                <div className="blog-image">
                  <img src={blog.image} alt={blog.title} />
                </div>
              )}
              <div className="blog-content">
                <div className="blog-header">
                  <h3>{blog.title}</h3>
                  <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                    {blog.published ? 'Published' : 'Draft'}
                  </span>
                </div>
                
                {blog.excerpt && (
                  <p className="blog-excerpt">{blog.excerpt}</p>
                )}
                
                <p className="blog-content-preview">
                  {blog.content.length > 100 
                    ? `${blog.content.substring(0, 100)}...` 
                    : blog.content
                  }
                </p>
                
                <div className="blog-meta">
                  <span>Category: {blog.category}</span>
                  <span>Created: {new Date(blog.createdAt).toLocaleDateString()}</span>
                </div>
                
                {blog.tags && blog.tags.length > 0 && (
                  <div className="blog-tags">
                    {blog.tags.map(tag => (
                      <span key={tag} className="tag">#{tag}</span>
                    ))}
                  </div>
                )}
                
                <div className="blog-actions">
                  <button 
                    className="btn-secondary"
                    onClick={() => togglePublish(blog)}
                  >
                    {blog.published ? 'Unpublish' : 'Publish'}
                  </button>
                  <button 
                    className="btn-edit"
                    onClick={() => setEditingBlog(blog)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn-danger"
                    onClick={() => handleDeleteBlog(blog.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default BlogsPage;
