// admin-panel/src/components/dashboard/Dashboard.js
import React, { useState, useEffect } from 'react';
import { dashboardAPI } from '../../services/api';
import StatsCard from './StatsCard';
import RecentActivity from './RecentActivity';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    totalProjects: 0,
    totalContacts: 0,
    publishedBlogs: 0,
    draftBlogs: 0,
    totalViews: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [recentActivity, setRecentActivity] = useState([]);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Load stats
      const statsData = await dashboardAPI.getStats();
      setStats(statsData);
      
      // Load recent activity
      const activityData = await dashboardAPI.getRecentActivity();
      setRecentActivity(activityData);
      
    } catch (err) {
      console.error('Failed to load dashboard data:', err);
      setError('Failed to load dashboard data. Please check your backend connection.');
    } finally {
      setLoading(false);
    }
  };

  const createTestData = async (type) => {
    try {
      let response;
      switch(type) {
        case 'blog':
          response = await dashboardAPI.createTestBlog();
          break;
        case 'project':
          response = await dashboardAPI.createTestProject();
          break;
        case 'contact':
          response = await dashboardAPI.createTestMessage();
          break;
        default:
          return;
      }
      
      if (response.success) {
        // Reload data to reflect changes
        await loadDashboardData();
        alert(`‚úÖ ${type} created successfully!`);
      }
    } catch (err) {
      console.error(`Failed to create ${type}:`, err);
      alert(`‚ùå Failed to create ${type}. Check console for details.`);
    }
  };

  const publishBlog = async (blogId) => {
    try {
      const response = await dashboardAPI.publishBlog(blogId);
      if (response.success) {
        await loadDashboardData();
      }
    } catch (err) {
      console.error('Failed to publish blog:', err);
    }
  };

  if (loading) {
    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
        </div>
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
        </div>
        <div className="error-state">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Connection Error</h3>
          <p>{error}</p>
          <button onClick={loadDashboardData} className="btn btn-primary">
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-title">
          <h1>Admin Dashboard</h1>
          <p className="subtitle">Real-time Content Management</p>
        </div>
        <div className="header-actions">
          <button onClick={loadDashboardData} className="btn btn-primary">
            üîÑ Refresh
          </button>
        </div>
      </div>

      {/* Statistics Grid */}
      <div className="stats-grid">
        <StatsCard
          title="Total Blogs"
          value={stats.totalBlogs}
          icon="üìù"
          color="primary"
          breakdown={[
            { label: 'Published', value: stats.publishedBlogs, type: 'published' },
            { label: 'Drafts', value: stats.draftBlogs, type: 'draft' }
          ]}
        />
        
        <StatsCard
          title="Total Projects"
          value={stats.totalProjects}
          icon="üíº"
          color="success"
        />
        
        <StatsCard
          title="Messages"
          value={stats.totalContacts}
          icon="‚úâÔ∏è"
          color="info"
        />
        
        <StatsCard
          title="Total Views"
          value={stats.totalViews}
          icon="üëÅÔ∏è"
          color="warning"
          trend="+12%"
        />
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button 
            onClick={() => createTestData('blog')} 
            className="btn btn-success"
          >
            üìù Create Test Blog
          </button>
          <button 
            onClick={() => createTestData('project')} 
            className="btn btn-success"
          >
            üíº Create Test Project
          </button>
          <button 
            onClick={() => createTestData('contact')} 
            className="btn btn-success"
          >
            ‚úâÔ∏è Create Test Message
          </button>
        </div>
      </div>

      {/* Recent Activity */}
      <RecentActivity activities={recentActivity} />

      {/* Debug Info */}
      <div className="debug-info">
        <details>
          <summary>API Status</summary>
          <pre>{JSON.stringify(stats, null, 2)}</pre>
        </details>
      </div>
    </div>
  );
}

export default Dashboard;
