import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ user, onLogout }) => {
    const location = useLocation();

    const menuItems = [
        { path: '/dashboard', label: 'Dashboard', icon: 'üìä', color: '#6366f1' },
        { path: '/blogs', label: 'Blog Posts', icon: 'üìù', color: '#8b5cf6' },
        { path: '/projects', label: 'Projects', icon: 'üíº', color: '#06b6d4' },
        { path: '/analytics', label: 'Analytics', icon: 'üìà', color: '#10b981' },
        { path: '/settings', label: 'Settings', icon: '‚öôÔ∏è', color: '#f59e0b' }
    ];

    return (
        <div className="sidebar">
            {/* Floating Elements */}
            <div className="floating-elements">
                <div className="floating-element"></div>
                <div className="floating-element"></div>
                <div className="floating-element"></div>
            </div>

            <div className="sidebar-header">
                <h2>Admin Pro</h2>
                <div className="user-info">
                    <div className="user-avatar">
                        {user?.name?.charAt(0) || 'A'}
                    </div>
                    <span className="user-name">Welcome, {user?.name || 'Admin'}</span>
                    <span className="user-email">{user?.email}</span>
                </div>
            </div>

            <nav className="sidebar-nav">
                <ul>
                    {menuItems.map((item, index) => (
                        <li key={item.path}>
                            <Link 
                                to={item.path} 
                                className={location.pathname === item.path ? 'active' : ''}
                                style={{
                                    '--item-color': item.color
                                }}
                            >
                                <span className="icon" style={{ color: item.color }}>
                                    {item.icon}
                                </span>
                                <span className="label">{item.label}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
            </nav>

            <div className="sidebar-footer">
                <button onClick={onLogout} className="logout-button">
                    <span>üö™</span>
                    <span>Logout</span>
                </button>
            </div>
        </div>
    );
};

export default Sidebar;
