import React, { useState, useEffect } from 'react';
import { blogAPI, projectAPI } from '../services/api';

const DashboardPage = () => {
  const [stats, setStats] = useState({
    blogs: { total: 0, published: 0, draft: 0 },
    projects: { total: 0, published: 0, draft: 0 }
  });
  const [recentBlogs, setRecentBlogs] = useState([]);
  const [recentProjects, setRecentProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch blogs and projects
      const [blogsResponse, projectsResponse] = await Promise.all([
        blogAPI.getBlogs(),
        projectAPI.getProjects()
      ]);

      const blogs = blogsResponse.data;
      const projects = projectsResponse.data;

      // Calculate stats
      const blogStats = {
        total: blogs.length,
        published: blogs.filter(blog => blog.status === 'published').length,
        draft: blogs.filter(blog => blog.status === 'draft').length
      };

      const projectStats = {
        total: projects.length,
        published: projects.filter(project => project.status === 'published').length,
        draft: projects.filter(project => project.status === 'draft').length
      };

      setStats({
        blogs: blogStats,
        projects: projectStats
      });

      // Get recent items (last 5)
      setRecentBlogs(blogs.slice(0, 5));
      setRecentProjects(projects.slice(0, 5));

    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="dashboard-loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard-page">
      <div className="dashboard-header">
        <h1>Dashboard</h1>
        <p>Welcome to your admin panel</p>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Blogs</h3>
          <div className="stat-number">{stats.blogs.total}</div>
          <div className="stat-details">
            <span className="published">{stats.blogs.published} Published</span>
            <span className="draft">{stats.blogs.draft} Draft</span>
          </div>
        </div>

        <div className="stat-card">
          <h3>Total Projects</h3>
          <div className="stat-number">{stats.projects.total}</div>
          <div className="stat-details">
            <span className="published">{stats.projects.published} Published</span>
            <span className="draft">{stats.projects.draft} Draft</span>
          </div>
        </div>

        <div className="stat-card">
          <h3>Total Content</h3>
          <div className="stat-number">{stats.blogs.total + stats.projects.total}</div>
          <div className="stat-details">
            <span>All published items</span>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="recent-activity">
        <div className="recent-section">
          <h3>Recent Blog Posts</h3>
          {recentBlogs.length === 0 ? (
            <p>No blog posts yet.</p>
          ) : (
            <div className="recent-list">
              {recentBlogs.map(blog => (
                <div key={blog._id} className="recent-item">
                  <div className="item-info">
                    <h4>{blog.title}</h4>
                    <span className={`status ${blog.status}`}>{blog.status}</span>
                  </div>
                  <span className="item-date">
                    {new Date(blog.createdAt).toLocaleDateString()}
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="recent-section">
          <h3>Recent Projects</h3>
          {recentProjects.length === 0 ? (
            <p>No projects yet.</p>
          ) : (
            <div className="recent-list">
              {recentProjects.map(project => (
                <div key={project._id} className="recent-item">
                  <div className="item-info">
                    <h4>{project.title}</h4>
                    <span className={`status ${project.status}`}>{project.status}</span>
                  </div>
                  <span className="item-date">
                    {new Date(project.createdAt).toLocaleDateString()}
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
