import React, { useState, useRef, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import './Header.css';

const Header = () => {
  const { user, logout } = useAuth();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const dropdownRef = useRef(null);
  const notificationRef = useRef(null);

  // Sample notifications
  useEffect(() => {
    setNotifications([
      { id: 1, message: 'New message from John Doe', time: '5 min ago', type: 'message', read: false },
      { id: 2, message: 'Blog post published successfully', time: '1 hour ago', type: 'success', read: true },
      { id: 3, message: 'System backup completed', time: '2 hours ago', type: 'info', read: true },
    ]);
  }, []);

  const handleLogout = () => {
    logout();
  };

  const handleProfileClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
    setIsNotificationOpen(false);
  };

  const handleNotificationClick = () => {
    setIsNotificationOpen(!isNotificationOpen);
    setIsDropdownOpen(false);
  };

  const handleNotificationRead = (id) => {
    setNotifications(notifications.map(notif => 
      notif.id === id ? { ...notif, read: true } : notif
    ));
  };

  const unreadCount = notifications.filter(notif => !notif.read).length;

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
      if (notificationRef.current && !notificationRef.current.contains(event.target)) {
        setIsNotificationOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header className="admin-header">
      {/* Left Section - Search and Title */}
      <div className="header-left">
        <div className="search-container">
          <input 
            type="text" 
            placeholder="Search..." 
            className="search-input"
          />
          <span className="search-icon">üîç</span>
        </div>
      </div>

      {/* Right Section - User Info and Actions */}
      <div className="header-right">
        {/* Notification Bell */}
        <div className="notification-container" ref={notificationRef}>
          <button 
            className={`notification-btn ${unreadCount > 0 ? 'has-notifications' : ''}`}
            onClick={handleNotificationClick}
          >
            <span className="notification-icon">üîî</span>
            {unreadCount > 0 && (
              <span className="notification-badge">{unreadCount}</span>
            )}
          </button>

          {/* Notification Dropdown */}
          {isNotificationOpen && (
            <div className="notification-dropdown">
              <div className="notification-header">
                <h4>Notifications</h4>
                <span className="notification-count">{unreadCount} unread</span>
              </div>
              
              <div className="notification-list">
                {notifications.length > 0 ? (
                  notifications.map(notification => (
                    <div 
                      key={notification.id} 
                      className={`notification-item ${notification.read ? 'read' : 'unread'}`}
                      onClick={() => handleNotificationRead(notification.id)}
                    >
                      <div className="notification-icon">
                        {notification.type === 'message' && 'üí¨'}
                        {notification.type === 'success' && '‚úÖ'}
                        {notification.type === 'info' && '‚ÑπÔ∏è'}
                      </div>
                      <div className="notification-content">
                        <p className="notification-message">{notification.message}</p>
                        <span className="notification-time">{notification.time}</span>
                      </div>
                      {!notification.read && <div className="unread-dot"></div>}
                    </div>
                  ))
                ) : (
                  <div className="no-notifications">
                    <span>No new notifications</span>
                  </div>
                )}
              </div>

              <div className="notification-footer">
                <button className="view-all-btn">View All Notifications</button>
              </div>
            </div>
          )}
        </div>

        {/* User Profile */}
        <div className="user-container" ref={dropdownRef}>
          <button 
            className="user-profile-btn"
            onClick={handleProfileClick}
          >
            <div className="user-avatar">
              {user?.avatar || (user?.name?.charAt(0) || 'A')}
            </div>
            <div className="user-info">
              <span className="user-name">{user?.name || 'Admin User'}</span>
              <span className="user-role">{user?.role || 'Administrator'}</span>
            </div>
            <span className={`dropdown-arrow ${isDropdownOpen ? 'open' : ''}`}>‚ñº</span>
          </button>

          {/* Profile Dropdown */}
          {isDropdownOpen && (
            <div className="profile-dropdown">
              <div className="dropdown-header">
                <div className="dropdown-avatar">
                  {user?.avatar || (user?.name?.charAt(0) || 'A')}
                </div>
                <div className="dropdown-user-info">
                  <span className="dropdown-name">{user?.name || 'Admin User'}</span>
                  <span className="dropdown-email">{user?.email || 'admin@example.com'}</span>
                </div>
              </div>

              <div className="dropdown-menu">
                <button className="dropdown-item">
                  <span className="item-icon">üë§</span>
                  <span>My Profile</span>
                </button>
                <button className="dropdown-item">
                  <span className="item-icon">‚öôÔ∏è</span>
                  <span>Settings</span>
                </button>
                <button className="dropdown-item">
                  <span className="item-icon">üõ°Ô∏è</span>
                  <span>Privacy & Security</span>
                </button>
                <div className="dropdown-divider"></div>
                <button 
                  className="dropdown-item logout-item"
                  onClick={handleLogout}
                >
                  <span className="item-icon">üö™</span>
                  <span>Logout</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
