import api from './api';

// Complete Admin API service
export const adminAPI = {
  // ✅ PROJECTS MANAGEMENT
  getProjects: () => api.get('/projects/admin/all'),
  createProject: (projectData) => api.post('/projects/admin/create', projectData),
  updateProject: (id, projectData) => api.put(`/projects/admin/update/${id}`, projectData),
  deleteProject: (id) => api.delete(`/projects/admin/delete/${id}`),
  
  // ✅ BLOG MANAGEMENT
  getBlogs: () => api.get('/blog/admin/all'),
  createBlog: (blogData) => api.post('/blog/admin/create', blogData),
  updateBlog: (id, blogData) => api.put(`/blog/admin/update/${id}`, blogData),
  deleteBlog: (id) => api.delete(`/blog/admin/delete/${id}`),
  updateBlogStatus: (id, status) => api.patch(`/blog/admin/update/${id}`, { published: status }),
  
  // ✅ DASHBOARD STATS
  getDashboardStats: () => api.get('/admin/stats'),
  
  // ✅ CONTACT MESSAGES
  getMessages: () => api.get('/contact'),
  getMessage: (id) => api.get(`/contact/${id}`),
  deleteMessage: (id) => api.delete(`/contact/${id}`),
  markMessageAsRead: (id) => api.patch(`/contact/${id}/read`),
  
  // ✅ AUTHENTICATION
  login: (credentials) => api.post('/auth/login', credentials),
  verify: () => api.get('/auth/me'),
  
  // ✅ USER MANAGEMENT
  getUsers: () => api.get('/admin/users'),
  updateUser: (id, userData) => api.put(`/admin/users/${id}`, userData),
};

export default adminAPI;
