import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    console.log(`ðŸ”„ API Request: ${config.method?.toUpperCase()} ${config.baseURL}${config.url}`);
    return config;
  },
  (error) => {
    console.error('âŒ Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Success: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('âŒ API Error:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message,
      data: error.response?.data
    });
    
    if (error.response?.status === 401) {
      console.log('ðŸ” Unauthorized - redirecting to login');
      localStorage.removeItem('adminToken');
      window.location.href = '/login';
    }
    
    return Promise.reject(error);
  }
);

// Enhanced API endpoints with better error handling
export const authAPI = {
  login: async (credentials) => {
    try {
      console.log('ðŸ” Attempting login with:', credentials.email);
      const response = await api.post('/auth/login', credentials);
      console.log('âœ… Login successful:', response.data);
      return response;
    } catch (error) {
      console.error('âŒ Login failed:', error.response?.data || error.message);
      throw error;
    }
  },
  
  verifyToken: async () => {
    try {
      const response = await api.get('/auth/verify');
      return response;
    } catch (error) {
      console.error('âŒ Token verification failed:', error.response?.data || error.message);
      throw error;
    }
  },
  
  logout: () => api.post('/auth/logout')
};

export const blogAPI = {
  getAll: (params) => api.get('/blog', { params }),
  getById: (id) => api.get(`/blog/${id}`),
  create: (data) => api.post('/blog', data),
  update: (id, data) => api.put(`/blog/${id}`, data),
  delete: (id) => api.delete(`/blog/${id}`),
};

export const projectAPI = {
  getAll: (params) => api.get('/projects', { params }),
  getById: (id) => api.get(`/projects/${id}`),
  create: (data) => api.post('/projects', data),
  update: (id, data) => api.put(`/projects/${id}`, data),
  delete: (id) => api.delete(`/projects/${id}`),
};

export const contactAPI = {
  getAll: (params) => api.get('/contact', { params }),
  getById: (id) => api.get(`/contact/${id}`),
  markRead: (id) => api.put(`/contact/${id}/read`),
  delete: (id) => api.delete(`/contact/${id}`),
};

// Dashboard API using your demo endpoint
export const dashboardAPI = {
  getStats: () => api.get('/admin/dashboard/stats'),
};

// Health check using your endpoint
export const checkBackendConnection = () => api.get('/health');

export default api;
