const fetchBlogs = async () => {
  try {
    setLoading(true);
    const response = await blogsAPI.getAll();
    
    // üêõ DEBUG: See what the API actually returns
    console.log('üîç DEBUG - Full API response:', response);
    console.log('üîç DEBUG - Response type:', typeof response);
    console.log('üîç DEBUG - Response data:', response?.data);
    console.log('üîç DEBUG - Is array:', Array.isArray(response));
    console.log('üîç DEBUG - Is array data:', Array.isArray(response?.data));
    
    // Safe data extraction with better debugging
    let blogsData = [];
    
    if (Array.isArray(response)) {
      console.log('‚úÖ Using response as array');
      blogsData = response;
    } else if (response && Array.isArray(response.data)) {
      console.log('‚úÖ Using response.data as array');
      blogsData = response.data;
    } else if (response && response.data) {
      console.log('‚úÖ Wrapping response.data in array');
      blogsData = [response.data];
    } else {
      console.log('‚ùå No valid data found, using empty array');
      blogsData = [];
    }
    
    console.log('‚úÖ Final blogs data:', blogsData);
    console.log('‚úÖ Final blogs data length:', blogsData.length);
    setBlogs(blogsData);
  } catch (error) {
    console.error('‚ùå Error fetching blogs:', error);
    console.error('‚ùå Error details:', error.message);
    setBlogs([]);
  } finally {
    setLoading(false);
  }
};
