import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    image: project?.image || '',
    featured: project?.featured || false,
    liveUrl: project?.liveUrl || '',
    githubUrl: project?.githubUrl || '',
    demoUrl: project?.demoUrl || ''
  });

  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(project?.image || '');
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
        setFormData(prev => ({ ...prev, image: e.target.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setImageFile(null);
    setImagePreview('');
    setFormData(prev => ({ ...prev, image: '' }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Simulate upload progress
    setUploadProgress(0);
    const progressInterval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 90) {
          clearInterval(progressInterval);
          return 90;
        }
        return prev + 10;
      });
    }, 100);

    try {
      // Prepare data for backend - ensure URLs are properly formatted
      const projectData = {
        name: formData.name.trim(),
        description: formData.description.trim(),
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: formData.image.trim(),
        featured: formData.featured,
        liveUrl: formData.liveUrl.trim(),
        githubUrl: formData.githubUrl.trim(),
        demoUrl: formData.demoUrl.trim()
      };
      
      // If editing, include the _id
      if (project?._id) {
        projectData._id = project._id;
      }

      console.log('ðŸ“¦ Sending project data to backend:', projectData);

      setUploadProgress(100);
      
      // Wait a bit to show 100% progress
      setTimeout(() => {
        onSave(projectData);
        setUploadProgress(0);
      }, 500);

    } catch (error) {
      console.error('Error uploading project:', error);
      setUploadProgress(0);
      clearInterval(progressInterval);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal project-manager-modal">
        <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Project Name *</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              placeholder="Enter project name"
              required
            />
          </div>

          <div className="form-group">
            <label>Description *</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              placeholder="Enter project description, features, and your role..."
              required
              rows="4"
            />
          </div>

          <div className="form-group">
            <label>Technologies Used *</label>
            <input
              type="text"
              value={formData.technologies}
              onChange={(e) => setFormData({...formData, technologies: e.target.value})}
              placeholder="React, Node.js, MongoDB (comma separated)"
              required
            />
          </div>

          {/* Project URLs Section */}
          <div className="urls-section">
            <h3>Project Links</h3>
            
            <div className="form-group">
              <label>Live Project URL</label>
              <input
                type="url"
                value={formData.liveUrl}
                onChange={(e) => setFormData({...formData, liveUrl: e.target.value})}
                placeholder="https://your-project.com"
              />
              <small className="input-hint">Link to the live deployed project</small>
            </div>

            <div className="form-group">
              <label>GitHub Repository URL</label>
              <input
                type="url"
                value={formData.githubUrl}
                onChange={(e) => setFormData({...formData, githubUrl: e.target.value})}
                placeholder="https://github.com/username/repository"
              />
              <small className="input-hint">Link to the source code repository</small>
            </div>

            <div className="form-group">
              <label>Demo Video URL</label>
              <input
                type="url"
                value={formData.demoUrl}
                onChange={(e) => setFormData({...formData, demoUrl: e.target.value})}
                placeholder="https://youtube.com/watch?v=..."
              />
              <small className="input-hint">Link to demo video (YouTube, Loom, etc.)</small>
            </div>
          </div>

          {/* Image Upload Section */}
          <div className="form-group">
            <label>Project Image</label>
            
            {imagePreview ? (
              <div className="image-preview-container">
                <div className="image-preview">
                  <img src={imagePreview} alt="Project preview" />
                  <button 
                    type="button" 
                    className="remove-image-btn"
                    onClick={removeImage}
                  >
                    <i className="fas fa-times"></i>
                  </button>
                </div>
                <p className="image-hint">Click the image to change</p>
              </div>
            ) : (
              <div className="image-upload-area">
                <input
                  type="file"
                  id="project-image"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="image-input"
                />
                <label htmlFor="project-image" className="upload-label">
                  <div className="upload-content">
                    <i className="fas fa-cloud-upload-alt"></i>
                    <p>Click to upload project image</p>
                    <span>PNG, JPG, WEBP up to 5MB</span>
                  </div>
                </label>
              </div>
            )}
          </div>

          {/* Fallback URL input for existing images */}
          {!imagePreview && !imageFile && (
            <div className="form-group">
              <label>Or enter image URL</label>
              <input
                type="url"
                value={formData.image}
                onChange={(e) => setFormData({...formData, image: e.target.value})}
                placeholder="https://example.com/project-image.jpg"
              />
            </div>
          )}

          <div className="form-group checkbox">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={formData.featured}
                onChange={(e) => setFormData({...formData, featured: e.target.checked})}
              />
              <span className="checkmark"></span>
              Featured Project
            </label>
            <p className="checkbox-hint">Featured projects will be highlighted on your portfolio</p>
          </div>

          {/* Upload Progress */}
          {uploadProgress > 0 && (
            <div className="upload-progress">
              <div className="progress-bar">
                <div 
                  className="progress-fill" 
                  style={{ width: `${uploadProgress}%` }}
                ></div>
              </div>
              <span className="progress-text">{uploadProgress}%</span>
            </div>
          )}

          <div className="form-actions">
            <button type="submit" className="btn-primary" disabled={uploadProgress > 0}>
              {uploadProgress > 0 ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i>
                  {project ? 'Updating...' : 'Creating...'}
                </>
              ) : (
                <>
                  <i className="fas fa-save"></i>
                  {project ? 'Update Project' : 'Create Project'}
                </>
              )}
            </button>
            <button type="button" className="btn-secondary" onClick={onCancel} disabled={uploadProgress > 0}>
              <i className="fas fa-times"></i>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
