// src/pages/ProjectsPage.js - Complete updated version
import React, { useState, useEffect } from 'react';
import { projectsAPI } from '../services/api'; // Use projectsAPI instead of adminAPI
import ProjectManager from '../components/ProjectManager';
import './ProjectsPage.css';

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [error, setError] = useState('');

  const fetchProjects = async () => {
    try {
      setLoading(true);
      console.log('üîÑ Fetching projects from real API...');
      
      const response = await projectsAPI.getProjects();
      console.log('üì¶ Real projects response:', response);
      
      // Handle different response formats
      if (Array.isArray(response)) {
        setProjects(response);
      } else if (response.data && Array.isArray(response.data)) {
        setProjects(response.data);
      } else if (response.success && response.data) {
        setProjects(response.data);
      } else {
        console.warn('Unexpected projects response format:', response);
        setProjects([]);
      }
      
      setError('');
    } catch (err) {
      console.error('‚ùå Error fetching projects:', err);
      setError('Failed to load projects: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProject = async (projectId) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        await projectsAPI.deleteProject(projectId);
        alert('Project deleted successfully!');
        fetchProjects();
      } catch (error) {
        alert('Error deleting project: ' + error.message);
      }
    }
  };

  const handleSaveProject = async (projectData) => {
    try {
      let response;
      if (projectData._id) {
        response = await projectsAPI.updateProject(projectData._id, projectData);
      } else {
        response = await projectsAPI.createProject(projectData);
      }
      
      alert('Project saved successfully!');
      setShowForm(false);
      setEditingProject(null);
      fetchProjects();
    } catch (error) {
      alert('Error saving project: ' + error.message);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  if (loading) return <div className="loading">Loading projects...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="projects-page">
      <div className="page-header">
        <h1>Project Management</h1>
        <button onClick={() => setShowForm(true)} className="btn-primary">
          + Add New Project
        </button>
      </div>

      {showForm && (
        <ProjectManager
          project={editingProject}
          onSave={handleSaveProject}
          onCancel={() => {
            setShowForm(false);
            setEditingProject(null);
          }}
        />
      )}

      <div className="projects-stats">
        <p>Total Projects: {projects.length}</p>
      </div>

      <div className="projects-grid">
        {projects.length === 0 ? (
          <div className="no-projects">
            <p>No projects found. Create your first project!</p>
          </div>
        ) : (
          projects.map(project => (
            <div key={project._id} className="project-card">
              <div className="project-content">
                <h3>{project.title || project.name}</h3>
                <p className="project-description">{project.description}</p>
                
                {project.technologies && project.technologies.length > 0 && (
                  <div className="technologies">
                    <strong>Technologies:</strong>
                    <div className="tech-tags">
                      {project.technologies.map((tech, index) => (
                        <span key={index} className="tech-tag">{tech}</span>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="project-meta">
                  <span className="project-id">ID: {project._id}</span>
                  {project.featured && <span className="featured-badge">‚≠ê Featured</span>}
                  <span className="date">
                    Created: {new Date(project.createdAt).toLocaleDateString()}
                  </span>
                </div>
                
                <div className="project-actions">
                  <button 
                    onClick={() => {
                      setEditingProject(project);
                      setShowForm(true);
                    }}
                    className="btn-edit"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDeleteProject(project._id)}
                    className="btn-delete"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProjectsPage;
