// pages/BlogsPage.js
import React, { useState, useEffect } from 'react';
import { blogsAPI, mockBlogAPI } from '../services/api';
import './BlogsPage.css';

const BlogsPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);

  const [newBlog, setNewBlog] = useState({
    title: '',
    content: '',
    category: '',
    tags: '',
    published: false
  });

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const blogData = await blogsAPI.getAll();
      setBlogs(blogData);
    } catch (err) {
      setError('Failed to fetch blogs');
      console.error('Error fetching blogs:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (e) => {
    e.preventDefault();
    try {
      const createdBlog = await blogsAPI.create({
        ...newBlog,
        tags: newBlog.tags.split(',').map(tag => tag.trim())
      });
      
      setBlogs(prev => [createdBlog, ...prev]);
      setNewBlog({
        title: '',
        content: '',
        category: '',
        tags: '',
        published: false
      });
      setShowCreateForm(false);
    } catch (err) {
      setError('Failed to create blog');
    }
  };

  const handleUpdateBlog = async (e) => {
    e.preventDefault();
    try {
      const updatedBlog = await blogsAPI.update(editingBlog.id, {
        ...editingBlog,
        tags: editingBlog.tags.split(',').map(tag => tag.trim())
      });
      
      setBlogs(prev => prev.map(blog => 
        blog.id === editingBlog.id ? updatedBlog : blog
      ));
      setEditingBlog(null);
    } catch (err) {
      setError('Failed to update blog');
    }
  };

  const handleDeleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await blogsAPI.delete(id);
        setBlogs(prev => prev.filter(blog => blog.id !== id));
      } catch (err) {
        setError('Failed to delete blog');
      }
    }
  };

  const togglePublish = async (blog) => {
    try {
      const updatedBlog = await blogsAPI.update(blog.id, {
        ...blog,
        published: !blog.published
      });
      
      setBlogs(prev => prev.map(b => 
        b.id === blog.id ? updatedBlog : b
      ));
    } catch (err) {
      setError('Failed to update blog status');
    }
  };

  if (loading) {
    return (
      <div className="blogs-page">
        <div className="loading">Loading blogs...</div>
      </div>
    );
  }

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Blog Management</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowCreateForm(true)}
        >
          + Create New Blog
        </button>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>Ã—</button>
        </div>
      )}

      {/* Create Blog Form */}
      {showCreateForm && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Create New Blog</h2>
            <form onSubmit={handleCreateBlog}>
              <div className="form-group">
                <label>Title</label>
                <input
                  type="text"
                  value={newBlog.title}
                  onChange={(e) => setNewBlog({...newBlog, title: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Content</label>
                <textarea
                  value={newBlog.content}
                  onChange={(e) => setNewBlog({...newBlog, content: e.target.value})}
                  required
                  rows="5"
                />
              </div>
              <div className="form-group">
                <label>Category</label>
                <input
                  type="text"
                  value={newBlog.category}
                  onChange={(e) => setNewBlog({...newBlog, category: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Tags (comma separated)</label>
                <input
                  type="text"
                  value={newBlog.tags}
                  onChange={(e) => setNewBlog({...newBlog, tags: e.target.value})}
                  placeholder="react, javascript, web"
                />
              </div>
              <div className="form-group checkbox">
                <label>
                  <input
                    type="checkbox"
                    checked={newBlog.published}
                    onChange={(e) => setNewBlog({...newBlog, published: e.target.checked})}
                  />
                  Published
                </label>
              </div>
              <div className="form-actions">
                <button type="submit" className="btn-primary">Create Blog</button>
                <button 
                  type="button" 
                  className="btn-secondary"
                  onClick={() => setShowCreateForm(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Edit Blog Form */}
      {editingBlog && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Edit Blog</h2>
            <form onSubmit={handleUpdateBlog}>
              <div className="form-group">
                <label>Title</label>
                <input
                  type="text"
                  value={editingBlog.title}
                  onChange={(e) => setEditingBlog({...editingBlog, title: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Content</label>
                <textarea
                  value={editingBlog.content}
                  onChange={(e) => setEditingBlog({...editingBlog, content: e.target.value})}
                  required
                  rows="5"
                />
              </div>
              <div className="form-group">
                <label>Category</label>
                <input
                  type="text"
                  value={editingBlog.category}
                  onChange={(e) => setEditingBlog({...editingBlog, category: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Tags (comma separated)</label>
                <input
                  type="text"
                  value={editingBlog.tags}
                  onChange={(e) => setEditingBlog({...editingBlog, tags: e.target.value})}
                />
              </div>
              <div className="form-group checkbox">
                <label>
                  <input
                    type="checkbox"
                    checked={editingBlog.published}
                    onChange={(e) => setEditingBlog({...editingBlog, published: e.target.checked})}
                  />
                  Published
                </label>
              </div>
              <div className="form-actions">
                <button type="submit" className="btn-primary">Update Blog</button>
                <button 
                  type="button" 
                  className="btn-secondary"
                  onClick={() => setEditingBlog(null)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Blogs List */}
      <div className="blogs-grid">
        {blogs.length === 0 ? (
          <div className="empty-state">
            <p>No blogs found. Create your first blog post!</p>
          </div>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="blog-card">
              <div className="blog-header">
                <h3>{blog.title}</h3>
                <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                  {blog.published ? 'Published' : 'Draft'}
                </span>
              </div>
              <div className="blog-meta">
                <span>Category: {blog.category}</span>
                <span>Created: {blog.createdAt}</span>
              </div>
              <p className="blog-content">
                {blog.content.length > 150 
                  ? `${blog.content.substring(0, 150)}...` 
                  : blog.content
                }
              </p>
              <div className="blog-tags">
                {blog.tags && blog.tags.map(tag => (
                  <span key={tag} className="tag">#{tag}</span>
                ))}
              </div>
              <div className="blog-actions">
                <button 
                  className="btn-secondary"
                  onClick={() => togglePublish(blog)}
                >
                  {blog.published ? 'Unpublish' : 'Publish'}
                </button>
                <button 
                  className="btn-edit"
                  onClick={() => setEditingBlog({...blog, tags: blog.tags?.join(', ') || ''})}
                >
                  Edit
                </button>
                <button 
                  className="btn-danger"
                  onClick={() => handleDeleteBlog(blog.id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default BlogsPage;
