import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    image: project?.image || '',
    featured: project?.featured || false
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file');
        return;
      }

      setSelectedFile(file);
    }
  };

  const handleUrlChange = (e) => {
    const url = e.target.value;
    setFormData({...formData, image: url});
    setSelectedFile(null);
  };

  // Upload function similar to BlogManager
  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      console.log('ðŸš€ Uploading project image to server...');
      
      const response = await fetch('http://localhost:5000/api/upload/image', {
        method: 'POST',
        body: formData,
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`Upload failed with status: ${response.status}`);
      }

      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.error || 'Upload failed');
      }

      console.log('âœ… Upload successful. Image URL:', data.imageUrl);
      return data.imageUrl;

    } catch (error) {
      console.error('âŒ Upload error:', error);
      throw new Error(`Upload failed: ${error.message}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // If no local file, save with existing URL
    if (!selectedFile) {
      const projectData = {
        name: formData.name,
        description: formData.description,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: formData.image,
        featured: formData.featured
      };
      
      if (project?._id) projectData._id = project._id;
      onSave(projectData);
      return;
    }

    // If local file selected, upload it
    setUploading(true);

    try {
      console.log('ðŸ“¤ Starting project image upload...');
      const imageUrl = await uploadImageToServer(selectedFile);
      console.log('ðŸŽ‰ Project image uploaded. URL:', imageUrl);
      
      const projectData = {
        name: formData.name,
        description: formData.description,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: imageUrl,
        featured: formData.featured
      };
      
      if (project?._id) projectData._id = project._id;
      
      console.log('ðŸ’¾ Saving project with image:', projectData.image);
      onSave(projectData);
      
    } catch (error) {
      alert(`Upload Error: ${error.message}`);
      console.error('Submit error:', error);
    } finally {
      setUploading(false);
    }
  };

  const clearImage = () => {
    setFormData({...formData, image: ''});
    setSelectedFile(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal project-manager-modal modern-modal">
        <div className="modal-header">
          <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
          <button 
            type="button" 
            className="close-btn" 
            onClick={onCancel}
            disabled={uploading}
          >
            <i className="fas fa-times"></i>
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="modern-form">
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">
                Project Name <span className="required">*</span>
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                placeholder="Enter project name"
                className="form-input"
                required
                disabled={uploading}
              />
            </div>

            <div className="form-group full-width">
              <label className="form-label">
                Description <span className="required">*</span>
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                placeholder="Enter project description, features, and your role..."
                className="form-textarea"
                required
                rows="4"
                disabled={uploading}
              />
            </div>

            <div className="form-group full-width">
              <label className="form-label">
                Technologies Used <span className="required">*</span>
              </label>
              <input
                type="text"
                value={formData.technologies}
                onChange={(e) => setFormData({...formData, technologies: e.target.value})}
                placeholder="React, Node.js, MongoDB (comma separated)"
                className="form-input"
                required
                disabled={uploading}
              />
              <div className="input-hint">
                Separate technologies with commas
              </div>
            </div>

            {/* Image Upload Section - Following BlogManager pattern */}
            <div className="form-group full-width">
              <label className="form-label">Project Image</label>

              <div className="image-upload-section">
                <label className="upload-label">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    className="file-input"
                    disabled={uploading}
                  />
                  <span className={`upload-button ${uploading ? 'uploading' : ''}`}>
                    {uploading ? 'Uploading...' : 'Choose Local Image'}
                  </span>
                </label>
                <span className="upload-hint">or</span>
              </div>

              <input
                type="url"
                value={formData.image}
                onChange={handleUrlChange}
                placeholder="Or enter image URL"
                className="url-input"
                disabled={uploading}
              />
              
              <div className="image-notes">
                <small>Supported formats: JPG, PNG, GIF, WebP â€¢ Max size: 5MB</small>
                {selectedFile && (
                  <div className="file-info">
                    <small>Selected: {selectedFile.name}</small>
                    <button 
                      type="button" 
                      className="clear-file-btn"
                      onClick={clearImage}
                      disabled={uploading}
                    >
                      Remove
                    </button>
                  </div>
                )}
                {formData.image && !selectedFile && (
                  <div className="file-info">
                    <small>Using URL: {formData.image}</small>
                    <button 
                      type="button" 
                      className="clear-file-btn"
                      onClick={clearImage}
                      disabled={uploading}
                    >
                      Remove
                    </button>
                  </div>
                )}
              </div>
            </div>

            <div className="form-group full-width">
              <div className="featured-toggle">
                <label className="toggle-label">
                  <div className="toggle-content">
                    <span className="toggle-text">Featured Project</span>
                    <span className="toggle-hint">
                      Featured projects will be highlighted on your portfolio
                    </span>
                  </div>
                  <div className="toggle-switch">
                    <input
                      type="checkbox"
                      checked={formData.featured}
                      onChange={(e) => setFormData({...formData, featured: e.target.checked})}
                      className="toggle-input"
                      disabled={uploading}
                    />
                    <span className="slider"></span>
                  </div>
                </label>
              </div>
            </div>
          </div>

          <div className="modal-actions">
            <button type="button" className="btn btn-outline" onClick={onCancel} disabled={uploading}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary" disabled={uploading}>
              {uploading ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i>
                  Uploading...
                </>
              ) : (
                <>
                  <i className="fas fa-save"></i>
                  {project ? 'Update Project' : 'Create Project'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
