import React, { useState } from 'react';
import { blogsAPI } from '../services/api';

const BlogManager = ({ blog, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    category: blog?.category || '',
    tags: blog?.tags?.join(', ') || '',
    published: blog?.published || false,
    image: null,
    imageUrl: blog?.image || ''
  });
  const [uploading, setUploading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUploading(true);

    try {
      const submitData = new FormData();
      submitData.append('title', formData.title);
      submitData.append('content', formData.content);
      submitData.append('excerpt', formData.excerpt);
      submitData.append('category', formData.category);
      submitData.append('tags', JSON.stringify(formData.tags.split(',').map(tag => tag.trim())));
      submitData.append('published', formData.published);
      
      if (formData.image) {
        submitData.append('image', formData.image);
      } else if (formData.imageUrl) {
        submitData.append('imageUrl', formData.imageUrl);
      }

      let result;
      if (blog) {
        result = await blogsAPI.update(blog._id, submitData);
      } else {
        result = await blogsAPI.create(submitData);
      }

      onSave(result.data);
    } catch (error) {
      console.error('Error saving blog:', error);
    } finally {
      setUploading(false);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ 
        ...prev, 
        image: file,
        imageUrl: URL.createObjectURL(file)
      }));
    }
  };

  const handleImageUrlChange = (e) => {
    setFormData(prev => ({ 
      ...prev, 
      imageUrl: e.target.value 
    }));
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{blog ? 'Edit Blog Post' : 'Create New Blog Post'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Excerpt</label>
            <textarea
              value={formData.excerpt}
              onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Content</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              rows="10"
              required
            />
          </div>

          <div className="form-group">
            <label>Category</label>
            <input
              type="text"
              value={formData.category}
              onChange={(e) => setFormData({...formData, category: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Tags (comma separated)</label>
            <input
              type="text"
              value={formData.tags}
              onChange={(e) => setFormData({...formData, tags: e.target.value})}
              placeholder="react, javascript, webdev"
            />
          </div>

          <div className="form-group">
            <label>Blog Image</label>
            
            <div className="image-upload-section">
              <label>Upload Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
              />
            </div>

            <div className="image-url-section">
              <label>Or use image URL:</label>
              <input
                type="url"
                value={formData.imageUrl}
                onChange={handleImageUrlChange}
                placeholder="https://example.com/image.jpg"
              />
            </div>

            {formData.imageUrl && (
              <div className="image-preview">
                <img src={formData.imageUrl} alt="Preview" style={{maxWidth: '200px'}} />
              </div>
            )}
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.published}
                onChange={(e) => setFormData({...formData, published: e.target.checked})}
              />
              Published
            </label>
          </div>

          <div className="form-actions">
            <button type="submit" disabled={uploading}>
              {uploading ? 'Saving...' : (blog ? 'Update' : 'Create')}
            </button>
            <button type="button" onClick={onCancel}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BlogManager;  // ✅ THIS LINE FIXES THE ERROR
