import React, { useState, useEffect } from 'react';
import { blogAPI, projectAPI, mockData } from '../services/api';
import Dashboard from '../components/Dashboard';

const DashboardPage = () => {
    const [stats, setStats] = useState({
        blogs: { total: 0, published: 0, draft: 0 },
        projects: { total: 0, published: 0, draft: 0 }
    });
    const [recentBlogs, setRecentBlogs] = useState([]);
    const [recentProjects, setRecentProjects] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            let blogs = [];
            let projects = [];

            try {
                const [blogsResponse, projectsResponse] = await Promise.all([
                    blogAPI.getBlogs(),
                    projectAPI.getProjects()
                ]);
                blogs = blogsResponse.data || [];
                projects = projectsResponse.data || [];
            } catch (error) {
                console.log('Using mock data for development');
                blogs = mockData.blogs;
                projects = mockData.projects;
            }

            const blogStats = {
                total: blogs.length,
                published: blogs.filter(blog => blog.status === 'published').length,
                draft: blogs.filter(blog => blog.status === 'draft').length
            };

            const projectStats = {
                total: projects.length,
                published: projects.filter(project => project.status === 'published').length,
                draft: projects.filter(project => project.status === 'draft').length
            };

            setStats({
                blogs: blogStats,
                projects: projectStats
            });

            setRecentBlogs(blogs.slice(0, 5));
            setRecentProjects(projects.slice(0, 5));

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="dashboard-loading">
                <div className="loading-spinner">
                    <div className="spinner"></div>
                    Loading Dashboard...
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-page">
            <div className="page-header">
                <h1>Dashboard Overview</h1>
                <p>Welcome back! Here's what's happening with your content.</p>
            </div>

            <Dashboard stats={stats} />

            <div className="recent-activity">
                <div className="recent-section">
                    <h3>Recent Blog Posts</h3>
                    {recentBlogs.length === 0 ? (
                        <p className="no-data">
                            No blog posts yet. <a href="/blogs">Create your first blog post</a>
                        </p>
                    ) : (
                        <div className="recent-list">
                            {recentBlogs.map(blog => (
                                <div key={blog._id} className="recent-item">
                                    <div className="item-info">
                                        <h4>{blog.title}</h4>
                                        <span className={`status ${blog.status}`}>
                                            {blog.status}
                                        </span>
                                    </div>
                                    <span className="item-date">
                                        {new Date(blog.createdAt).toLocaleDateString()}
                                    </span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                <div className="recent-section">
                    <h3>Recent Projects</h3>
                    {recentProjects.length === 0 ? (
                        <p className="no-data">
                            No projects yet. <a href="/projects">Create your first project</a>
                        </p>
                    ) : (
                        <div className="recent-list">
                            {recentProjects.map(project => (
                                <div key={project._id} className="recent-item">
                                    <div className="item-info">
                                        <h4>{project.title}</h4>
                                        <span className={`status ${project.status}`}>
                                            {project.status}
                                        </span>
                                    </div>
                                    <span className="item-date">
                                        {new Date(project.createdAt).toLocaleDateString()}
                                    </span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
