// services/api.js

// Mock data functions - no real API calls for now
export const authAPI = {
  demoLogin: async (credentials) => {
    await new Promise(resolve => setTimeout(resolve, 1500));

    if (credentials.username === 'admin' && credentials.password === 'admin123') {
      return {
        success: true,
        token: 'demo-token-' + Date.now(),
        user: { 
          id: 1, 
          username: 'admin', 
          role: 'administrator' 
        }
      };
    } else {
      throw new Error('Invalid credentials. Use: admin / admin123');
    }
  },

  logout: async () => {
    return { success: true };
  },

  verifyToken: async () => {
    return { valid: true };
  }
};

export const blogsAPI = {
  getAll: async () => {
    await new Promise(resolve => setTimeout(resolve, 1000));
    return [
      {
        id: 1,
        title: 'Getting Started with React',
        content: 'Learn the basics of React development with this comprehensive guide.',
        author: 'Admin User',
        published: true,
        createdAt: '2024-01-15',
        category: 'React',
        tags: ['react', 'javascript', 'frontend']
      },
      {
        id: 2,
        title: 'Advanced Node.js Patterns',
        content: 'Deep dive into Node.js architecture and design patterns.',
        author: 'Admin User',
        published: false,
        createdAt: '2024-01-10',
        category: 'Node.js',
        tags: ['nodejs', 'backend', 'javascript']
      },
      {
        id: 3,
        title: 'CSS Grid vs Flexbox',
        content: 'Understanding when to use CSS Grid and when to use Flexbox.',
        author: 'Admin User',
        published: true,
        createdAt: '2024-01-05',
        category: 'CSS',
        tags: ['css', 'frontend', 'layout']
      }
    ];
  },

  getById: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    const blogs = await blogsAPI.getAll();
    return blogs.find(blog => blog.id === parseInt(id)) || null;
  },

  create: async (blogData) => {
    await new Promise(resolve => setTimeout(resolve, 800));
    return {
      id: Date.now(),
      ...blogData,
      createdAt: new Date().toISOString().split('T')[0],
      author: 'Admin User'
    };
  },

  update: async (id, blogData) => {
    await new Promise(resolve => setTimeout(resolve, 600));
    return {
      id: parseInt(id),
      ...blogData,
      updatedAt: new Date().toISOString()
    };
  },

  delete: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400));
    return { success: true, message: 'Blog deleted successfully' };
  }
};

export const projectsAPI = {
  getAll: async () => {
    await new Promise(resolve => setTimeout(resolve, 1000));
    return [
      {
        id: 1,
        name: 'E-Commerce Platform',
        description: 'Full-stack e-commerce solution with React and Node.js',
        status: 'completed',
        technologies: ['React', 'Node.js', 'MongoDB', 'Stripe'],
