      },
      {
        id: 2,
        name: 'Task Management App',
        description: 'Collaborative task management application',
        status: 'active',
        technologies: ['Vue.js', 'Express', 'PostgreSQL'],
        demoUrl: 'https://demo-tasks.example.com',
        githubUrl: 'https://github.com/username/taskapp',
        createdAt: '2024-01-08'
      },
      {
        id: 3,
        name: 'Weather Dashboard',
        description: 'Real-time weather information dashboard',
        status: 'pending',
        technologies: ['React', 'Chart.js', 'Weather API'],
        demoUrl: 'https://demo-weather.example.com',
        githubUrl: 'https://github.com/username/weather',
        createdAt: '2024-01-03'
      }
    ];
  },

  getById: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    const projects = await projectsAPI.getAll();
    return projects.find(project => project.id === parseInt(id)) || null;
  },

  create: async (projectData) => {
    await new Promise(resolve => setTimeout(resolve, 800));
    return {
      id: Date.now(),
      ...projectData,
      createdAt: new Date().toISOString().split('T')[0]
    };
  },

  update: async (id, projectData) => {
    await new Promise(resolve => setTimeout(resolve, 600));
    return {
      id: parseInt(id),
      ...projectData,
      updatedAt: new Date().toISOString()
    };
  },

  delete: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400));
    return { success: true, message: 'Project deleted successfully' };
  }
};

export const dashboardAPI = {
  getStats: async () => {
    await new Promise(resolve => setTimeout(resolve, 800));

    // Get mock data to calculate stats
    const blogs = await blogsAPI.getAll();
    const projects = await projectsAPI.getAll();

    return {
      totalBlogs: blogs.length,
      totalProjects: projects.length,
      publishedBlogs: blogs.filter(blog => blog.published).length,
      activeProjects: projects.filter(project => project.status === 'active').length,
