        user: 'admin',
        timestamp: new Date(Date.now() - 7200000).toISOString(),
      },
      {
        id: 4,
        action: 'blog_updated',
        title: 'Blog "Advanced Node.js Patterns" updated',
        user: 'admin',
        timestamp: new Date(Date.now() - 10800000).toISOString(),
      },
    ];
  }
};

// Mock APIs for components that need them
export const mockBlogAPI = blogsAPI;
export const mockProjectAPI = projectsAPI;

// Default export
export default {
  authAPI,
  blogsAPI,
  projectsAPI,
  dashboardAPI,
  mockBlogAPI,
  mockProjectAPI
};
