import React, { useState, useEffect } from 'react';
import { adminApi } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    totalProjects: 0,
    totalContacts: 0,
    recentBlogs: [],
    recentProjects: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch data in parallel
      const [blogsData, projectsData, contactsData] = await Promise.all([
        adminApi.getBlogs(1, 5), // Get first 5 blogs
        adminApi.getProjects(),
        adminApi.getContacts().catch(() => []) // Handle if contacts API not implemented
      ]);

      setStats({
        totalBlogs: blogsData.totalBlogs || blogsData.blogs?.length || 0,
        totalProjects: projectsData.length || 0,
        totalContacts: contactsData.length || 0,
        recentBlogs: blogsData.blogs || blogsData || [],
        recentProjects: projectsData.slice(0, 3) || []
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const refreshData = () => {
    setLoading(true);
    setError('');
    fetchDashboardData();
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-error">
        <p>{error}</p>
        <button onClick={refreshData} className="btn-retry">
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Dashboard Overview</h1>
        <button onClick={refreshData} className="btn-refresh">
          Refresh Data
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìù</div>
          <div className="stat-info">
            <h3>Total Blogs</h3>
            <div className="stat-number">{stats.totalBlogs}</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üíº</div>
          <div className="stat-info">
            <h3>Total Projects</h3>
            <div className="stat-number">{stats.totalProjects}</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <h3>Contact Messages</h3>
            <div className="stat-number">{stats.totalContacts}</div>
          </div>
        </div>
      </div>

      {/* Recent Content */}
      <div className="recent-content">
        <div className="recent-section">
          <h3>Recent Blogs</h3>
          <div className="content-list">
            {stats.recentBlogs.length > 0 ? (
              stats.recentBlogs.map(blog => (
                <div key={blog._id} className="content-item">
                  <div className="content-main">
                    <span className="content-title">{blog.title}</span>
                    <span className={`content-status ${blog.published ? 'published' : 'draft'}`}>
                      {blog.published ? 'Published' : 'Draft'}
                    </span>
                  </div>
                  <span className="content-date">
                    {new Date(blog.createdAt).toLocaleDateString()}
                  </span>
                </div>
              ))
            ) : (
              <p className="no-data">No blogs yet</p>
            )}
          </div>
        </div>

        <div className="recent-section">
          <h3>Recent Projects</h3>
          <div className="content-list">
            {stats.recentProjects.length > 0 ? (
              stats.recentProjects.map(project => (
                <div key={project._id} className="content-item">
                  <div className="content-main">
                    <span className="content-title">{project.title}</span>
                    <span className={`content-status ${project.published ? 'published' : 'draft'}`}>
                      {project.published ? 'Published' : 'Draft'}
                    </span>
                  </div>
                </div>
              ))
            ) : (
              <p className="no-data">No projects yet</p>
            )}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button 
            className="action-btn"
            onClick={() => window.location.href = '/admin-panel/blogs'}
          >
            + New Blog
          </button>
          <button 
            className="action-btn"
            onClick={() => window.location.href = '/admin-panel/projects'}
          >
            + New Project
          </button>
          <a 
            href="/" 
            target="_blank" 
            rel="noopener noreferrer"
            className="action-btn preview-btn"
          >
            üëÅÔ∏è Live Preview
          </a>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
