import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
});

// Add auth token to requests
API.interceptors.request.use((req) => {
  if (localStorage.getItem('adminToken')) {
    req.headers.Authorization = `Bearer ${localStorage.getItem('adminToken')}`;
  }
  return req;
});

// Blog API functions
export const blogAPI = {
  // Create new blog post
  createBlog: (blogData) => API.post('/blog', blogData),
  
  // Get all blogs for admin
  getBlogs: () => API.get('/blog/admin/all'),
  
  // Update blog post
  updateBlog: (id, blogData) => API.put(`/blog/${id}`, blogData),
  
  // Delete blog post
  deleteBlog: (id) => API.delete(`/blog/${id}`),
};

// Auth API functions
export const authAPI = {
  login: (credentials) => API.post('/auth/admin/login', credentials),
};

// Admin Dashboard API functions - ADD THIS SECTION
export const adminApi = {
  // Dashboard statistics
  getDashboardStats: () => API.get('/admin/dashboard/stats'),
  
  // Get recent activities
  getRecentActivities: () => API.get('/admin/dashboard/activities'),
  
  // Get system status
  getSystemStatus: () => API.get('/admin/dashboard/system-status'),
  
  // User management (if needed)
  getUsers: () => API.get('/admin/users'),
  
  // Get blog statistics
  getBlogStats: () => API.get('/admin/stats/blogs'),
};
