import React, { useState, useEffect } from 'react';
import { adminBlogAPI } from '../services/api';

const BlogManager = () => {
    const [blogs, setBlogs] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editingBlog, setEditingBlog] = useState(null);
    const [loading, setLoading] = useState(false);
    const [formLoading, setFormLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        excerpt: '',
        tags: '',
        published: false,
        featuredImage: null
    });

    useEffect(() => {
        fetchBlogs();
    }, [currentPage]);

    const fetchBlogs = async () => {
        setLoading(true);
        try {
            const response = await adminBlogAPI.getAll(currentPage, 10);
            setBlogs(response.data.blogs);
            setTotalPages(response.data.totalPages);
        } catch (error) {
            console.error('Error fetching blogs:', error);
            alert('Error fetching blogs');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormLoading(true);

        try {
            const submitData = {
                ...formData,
                tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
            };

            if (editingBlog) {
                await adminBlogAPI.update(editingBlog._id, submitData);
            } else {
                await adminBlogAPI.create(submitData);
            }

            resetForm();
            fetchBlogs();
            alert(editingBlog ? 'Blog updated successfully!' : 'Blog created successfully!');
        } catch (error) {
            console.error('Error saving blog:', error);
            alert(error.response?.data?.message || 'Error saving blog');
        } finally {
            setFormLoading(false);
        }
    };

    const handleEdit = (blog) => {
        setEditingBlog(blog);
        setFormData({
            title: blog.title,
            content: blog.content,
            excerpt: blog.excerpt,
            tags: blog.tags.join(', '),
            published: blog.published,
            featuredImage: null
        });
        setShowForm(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this blog?')) {
            try {
                await adminBlogAPI.delete(id);
                fetchBlogs();
                alert('Blog deleted successfully!');
            } catch (error) {
                console.error('Error deleting blog:', error);
                alert('Error deleting blog');
            }
        }
    };

    const resetForm = () => {
        setFormData({
            title: '',
            content: '',
            excerpt: '',
            tags: '',
            published: false,
            featuredImage: null
        });
        setEditingBlog(null);
        setShowForm(false);
    };

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : 
                    type === 'file' ? files[0] : value
        });
    };

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    return (
        <div className="blog-manager">
            <div className="header">
                <h2>Blog Management</h2>
                <button onClick={() => setShowForm(true)} className="btn-primary">
                    Add New Blog
                </button>
            </div>

            {showForm && (
                <div className="form-modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h3>{editingBlog ? 'Edit Blog' : 'Create Blog'}</h3>
                            <button onClick={resetForm} className="close-btn">&times;</button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>Title *</label>
                                <input
                                    type="text"
                                    name="title"
                                    value={formData.title}
                                    onChange={handleChange}
                                    required
                                    placeholder="Enter blog title"
                                />
                            </div>
                            <div className="form-group">
                                <label>Excerpt *</label>
                                <textarea
                                    name="excerpt"
                                    value={formData.excerpt}
                                    onChange={handleChange}
                                    required
                                    placeholder="Short description of the blog"
                                    rows="3"
                                />
                            </div>
                            <div className="form-group">
                                <label>Content *</label>
                                <textarea
                                    name="content"
                                    value={formData.content}
                                    onChange={handleChange}
                                    rows="10"
                                    required
                                    placeholder="Write your blog content here..."
                                />
                            </div>
                            <div className="form-group">
                                <label>Tags</label>
                                <input
                                    type="text"
                                    name="tags"
                                    value={formData.tags}
                                    onChange={handleChange}
                                    placeholder="technology, programming, web development"
                                />
                                <small>Separate tags with commas</small>
                            </div>
                            <div className="form-group">
                                <label>Featured Image</label>
                                <input
                                    type="file"
                                    name="featuredImage"
                                    onChange={handleChange}
                                    accept="image/*"
                                />
                            </div>
                            <div className="form-group checkbox">
                                <label>
                                    <input
                                        type="checkbox"
                                        name="published"
                                        checked={formData.published}
                                        onChange={handleChange}
                                    />
                                    Publish immediately
                                </label>
                            </div>
                            <div className="form-actions">
                                <button type="submit" disabled={formLoading} className="btn-primary">
                                    {formLoading ? 'Saving...' : (editingBlog ? 'Update Blog' : 'Create Blog')}
                                </button>
                                <button type="button" onClick={resetForm} className="btn-secondary">
                                    Cancel
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            <div className="blogs-list">
                {loading ? (
                    <div className="loading">Loading blogs...</div>
                ) : (
                    <>
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Title</th>
                                    <th>Status</th>
                                    <th>Date</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {blogs.map(blog => (
                                    <tr key={blog._id}>
                                        <td>
                                            <div className="blog-title-cell">
                                                <strong>{blog.title}</strong>
                                                {blog.featuredImage && (
                                                    <small>Has image</small>
                                                )}
                                            </div>
                                        </td>
                                        <td>
                                            <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                                                {blog.published ? 'Published' : 'Draft'}
                                            </span>
                                        </td>
                                        <td>
                                            {new Date(blog.createdAt).toLocaleDateString()}
                                        </td>
                                        <td className="actions">
                                            <button 
                                                onClick={() => handleEdit(blog)} 
                                                className="btn-edit"
                                            >
                                                Edit
                                            </button>
                                            <button 
                                                onClick={() => handleDelete(blog._id)} 
                                                className="btn-delete"
                                            >
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                        {/* Pagination */}
                        {totalPages > 1 && (
                            <div className="pagination">
                                <button 
                                    disabled={currentPage === 1}
                                    onClick={() => handlePageChange(currentPage - 1)}
                                >
                                    Previous
                                </button>
                                <span>Page {currentPage} of {totalPages}</span>
                                <button 
                                    disabled={currentPage === totalPages}
                                    onClick={() => handlePageChange(currentPage + 1)}
                                >
                                    Next
                                </button>
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default BlogManager;
