import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Clear local storage and redirect to login if token is invalid
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  logout: () => api.post('/auth/logout'),
  checkAuth: () => api.get('/auth/me'),
};

export const blogAPI = {
  getBlogs: () => api.get('/blogs'),
  getBlog: (id) => api.get(`/blogs/${id}`),
  createBlog: (blogData) => api.post('/blogs', blogData),
  updateBlog: (id, blogData) => api.put(`/blogs/${id}`, blogData),
  deleteBlog: (id) => api.delete(`/blogs/${id}`),
  uploadImage: (imageData) => api.post('/blogs/upload', imageData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
};

export const projectAPI = {
  getProjects: () => api.get('/projects'),
  getProject: (id) => api.get(`/projects/${id}`),
  createProject: (projectData) => api.post('/projects', projectData),
  updateProject: (id, projectData) => api.put(`/projects/${id}`, projectData),
  deleteProject: (id) => api.delete(`/projects/${id}`),
  uploadImage: (imageData) => api.post('/projects/upload', imageData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
};

// Mock data for testing without backend
export const mockData = {
  blogs: [
    {
      _id: '1',
      title: 'Welcome to the Admin Panel',
      content: 'This is your first blog post. You can edit or delete it.',
      excerpt: 'Introduction to the admin panel features...',
      status: 'published',
      featuredImage: '',
      tags: ['admin', 'introduction'],
      createdAt: new Date().toISOString()
    }
  ],
  projects: [
    {
      _id: '1',
      title: 'Sample Project',
      description: 'This is a sample project description.',
      content: 'Detailed project information goes here...',
      technologies: ['React', 'Node.js'],
      projectUrl: 'https://example.com',
      githubUrl: 'https://github.com/example',
      status: 'published',
      featuredImage: '',
      category: 'Web Application',
      createdAt: new Date().toISOString()
    }
  ]
};

export default api;
