import React, { useState } from 'react';
import { blogsAPI } from '../services/api';

const BlogManager = ({ blog, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    category: blog?.category || '',
    tags: blog?.tags?.join(', ') || '',
    published: blog?.published || false,
    image: null,
    imageUrl: blog?.image || ''
  });
  const [uploading, setUploading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUploading(true);

    try {
      const submitData = new FormData();
      submitData.append('title', formData.title);
      submitData.append('content', formData.content);
      submitData.append('excerpt', formData.excerpt);
      submitData.append('category', formData.category);
      submitData.append('tags', JSON.stringify(formData.tags.split(',').map(tag => tag.trim())));
      submitData.append('published', formData.published);
      
      if (formData.image) {
        submitData.append('image', formData.image);
      } else if (formData.imageUrl) {
        submitData.append('imageUrl', formData.imageUrl);
      }

      let result;
      if (blog) {
        result = await blogsAPI.update(blog._id, submitData);
      } else {
        result = await blogsAPI.create(submitData);
      }

      onSave(result.data);
    } catch (error) {
      console.error('Error saving blog:', error);
    } finally {
      setUploading(false);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ 
        ...prev, 
        image: file,
        imageUrl: URL.createObjectURL(file)
      }));
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{blog ? 'Edit Blog Post' : 'Create New Blog Post'}</h2>
        <form onSubmit={handleSubmit}>
          {/* Form fields for title, content, excerpt, category, tags */}
          
          <div className="form-group">
            <label>Blog Image</label>
            
            <div className="image-upload-section">
              <label>Upload Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
              />
            </div>

            <div className="image-url-section">
              <label>Or use image URL:</label>
              <input
                type="url"
                value={formData.imageUrl}
                onChange={(e) => setFormData({...formData, imageUrl: e.target.value})}
                placeholder="https://example.com/image.jpg"
              />
            </div>

            {formData.imageUrl && (
              <div className="image-preview">
                <img src={formData.imageUrl} alt="Preview" style={{maxWidth: '200px'}} />
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="submit" disabled={uploading}>
              {uploading ? 'Saving...' : (blog ? 'Update' : 'Create')}
            </button>
            <button type="button" onClick={onCancel}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};
