// src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('authToken');
      localStorage.removeItem('isAuthenticated');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Dashboard API
export const dashboardAPI = {
  getStats: async () => {
    try {
      const response = await api.get('/api/admin/dashboard/stats');
      return response.data;
    } catch (error) {
      console.error('Dashboard stats error:', error);
      // Fallback to individual counts
      return await getFallbackStats();
    }
  },
};

// Blogs API
export const blogsAPI = {
  getBlogs: async () => {
    try {
      const response = await api.get('/api/admin/blogs');
      return response.data;
    } catch (error) {
      console.error('Get blogs error:', error);
      throw error;
    }
  },

  createBlog: async (blogData) => {
    try {
      const response = await api.post('/api/admin/blogs', blogData);
      return response.data;
    } catch (error) {
      console.error('Create blog error:', error);
      throw error;
    }
  },

  updateBlog: async (id, blogData) => {
    try {
      const response = await api.put(`/api/admin/blogs/${id}`, blogData);
      return response.data;
    } catch (error) {
      console.error('Update blog error:', error);
      throw error;
    }
  },

  deleteBlog: async (id) => {
    try {
      const response = await api.delete(`/api/admin/blogs/${id}`);
      return response.data;
    } catch (error) {
      console.error('Delete blog error:', error);
      throw error;
    }
  }
};

// Projects API
export const projectsAPI = {
  getProjects: async () => {
    try {
      const response = await api.get('/api/admin/projects');
      return response.data;
    } catch (error) {
      console.error('Get projects error:', error);
      throw error;
    }
  },

  createProject: async (projectData) => {
    try {
      const response = await api.post('/api/admin/projects', projectData);
      return response.data;
    } catch (error) {
      console.error('Create project error:', error);
      throw error;
    }
  },

  updateProject: async (id, projectData) => {
    try {
      const response = await api.put(`/api/admin/projects/${id}`, projectData);
      return response.data;
    } catch (error) {
      console.error('Update project error:', error);
      throw error;
    }
  },

  deleteProject: async (id) => {
    try {
      const response = await api.delete(`/api/admin/projects/${id}`);
      return response.data;
    } catch (error) {
      console.error('Delete project error:', error);
      throw error;
    }
  }
};

// Messages/Contacts API
export const messagesAPI = {
  getMessages: async () => {
    try {
      const response = await api.get('/api/admin/messages');
      return response.data;
    } catch (error) {
      console.error('Get messages error:', error);
      throw error;
    }
  },

  getContacts: async () => {
    try {
      const response = await api.get('/api/admin/contacts');
      return response.data;
    } catch (error) {
      console.error('Get contacts error:', error);
      throw error;
    }
  }
};

// Auth API
export const authAPI = {
  login: async (credentials) => {
    try {
      const response = await api.post('/api/admin/login', credentials);
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },

  logout: async () => {
    try {
      const response = await api.post('/api/admin/logout');
      return response.data;
    } catch (error) {
      console.error('Logout error:', error);
      throw error;
    }
  },

  verify: async () => {
    try {
      const response = await api.get('/api/admin/verify');
      return response.data;
    } catch (error) {
      console.error('Verify error:', error);
      throw error;
    }
  }
};

// Helper function for fallback stats
const getFallbackStats = async () => {
  try {
    const [blogsResponse, projectsResponse, messagesResponse] = await Promise.all([
      blogsAPI.getBlogs().catch(() => ({ data: [] })),
      projectsAPI.getProjects().catch(() => ({ data: [] })),
      messagesAPI.getMessages().catch(() => ({ data: [] }))
    ]);

    const blogsArray = extractArray(blogsResponse);
    const projectsArray = extractArray(projectsResponse);
    const messagesArray = extractArray(messagesResponse);

    return {
      stats: {
        totalBlogs: blogsArray.length,
        totalProjects: projectsArray.length,
        totalContacts: messagesArray.length,
        publishedBlogs: blogsArray.filter(blog => blog.status === 'published' || blog.published).length,
