export const projectsAPI = {
  getAll: async () => {
    try {
      const response = await apiCall('/api/projects'); // ✅ ADDED /api
      return response.data;
    } catch (error) {
      console.warn('Using mock project data');
      return [
        {
          id: 1,
          name: 'E-Commerce Platform',
          description: 'Full-stack e-commerce solution with React and Node.js',
          status: 'completed',
          technologies: ['React', 'Node.js', 'MongoDB', 'Stripe'],
          demoUrl: 'https://demo-ecommerce.example.com',
          githubUrl: 'https://github.com/username/ecommerce',
          image: 'https://via.placeholder.com/600x400/667eea/white?text=E-Commerce',
          featured: true,
          createdAt: '2024-01-12'
        }
      ];
    }
  },
  
  getById: async (id) => {
    try {
      const response = await apiCall(`/api/projects/${id}`); // ✅ ADDED /api
      return response.data;
    } catch (error) {
      console.warn('Project not found');
      return null;
    }
  },
  
  create: async (projectData) => {
    try {
      const response = await apiCall('/api/projects/admin/create', { // ✅ ADDED /api
        method: 'POST',
        body: JSON.stringify(projectData),
      });
      return response.data;
    } catch (error) {
      console.warn('Failed to create project, using mock data');
      return {
        id: Date.now(),
        ...projectData,
        createdAt: new Date().toISOString()
      };
    }
  },
  
  update: async (id, projectData) => {
    try {
      const response = await apiCall(`/api/projects/admin/update/${id}`, { // ✅ ADDED /api
        method: 'PUT',
        body: JSON.stringify(projectData),
      });
      return response.data;
    } catch (error) {
      console.warn('Failed to update project');
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      const response = await apiCall(`/api/projects/admin/delete/${id}`, { // ✅ ADDED /api
        method: 'DELETE',
      });
      return response;
    } catch (error) {
      console.warn('Failed to delete project');
      throw error;
    }
  }
};
