// admin-panel/src/services/api.js - UPDATED FOR PRODUCTION
import axios from 'axios';

// Production backend URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'https://zmo-backend.vercel.app/api';

console.log('ðŸ”§ Production API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
  timeout: 15000,
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  console.log(`ðŸš€ ${config.method?.toUpperCase()} to: ${config.url}`);
  return config;
});

// Handle responses globally
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.status} from: ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    console.error(`âŒ API Error:`, {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    
    if (error.response?.status === 401) {
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      // Use window.location for absolute path in production
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// --- Authentication API ---
export const authAPI = {
  login: async (email, password) => {
    try {
      console.log('ðŸ” Attempting login...');
      const res = await api.post('/auth/login', { email, password });
      return res.data;
    } catch (error) {
      console.error('âŒ Login failed:', error);
      throw error;
    }
  },
  
  logout: async () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminUser');
    return { message: 'Logout successful' };
  },
  
  verify: async () => {
    try {
      const res = await api.get('/auth/verify');
      return res.data;
    } catch (error) {
      console.error('âŒ Token verification failed:', error);
      throw error;
    }
  },
};

// --- Blog API ---
export const blogAPI = {
  getAll: async (page = 1, limit = 50) => {
    try {
      console.log('ðŸ“¡ Fetching blogs from backend...');
      const res = await api.get('/admin/blogs');
      
      // Handle response structure
      const responseData = res.data;
      
      // Return array directly (backend returns array)
      if (Array.isArray(responseData)) {
        return responseData;
      }
      
      // Handle wrapped responses
      if (responseData && Array.isArray(responseData.data)) {
        return responseData.data;
      }
      if (responseData && Array.isArray(responseData.blogs)) {
        return responseData.blogs;
      }
      
      console.warn('âš ï¸ Unexpected blog response format:', responseData);
      return [];
      
    } catch (error) {
      console.error('âŒ Failed to fetch blogs:', error);
      throw error;
    }
  },
  
  create: async (data) => {
    try {
      console.log('ðŸ“ Creating blog via backend...', data);
      const res = await api.post('/admin/blogs', data);
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to create blog:', error);
      throw error;
    }
  },
  
  update: async (id, data) => {
    try {
      console.log('âœï¸ Updating blog via backend...', id, data);
      const res = await api.put(`/admin/blogs/${id}`, data);
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to update blog:', error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      console.log('ðŸ—‘ï¸ Deleting blog via backend...', id);
      const res = await api.delete(`/admin/blogs/${id}`);
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to delete blog:', error);
      throw error;
    }
  },
  
  getById: async (id) => {
    try {
      const res = await api.get(`/admin/blogs/${id}`);
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to fetch blog:', error);
      throw error;
    }
  },
};

// --- Dashboard API ---
export const dashboardAPI = {
  getStats: async () => {
    try {
      const res = await api.get('/admin/dashboard/stats');
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to fetch stats:', error);
      throw error;
    }
  },
  
  getRecentActivity: async () => {
    try {
      const res = await api.get('/admin/dashboard/recent');
      return res.data;
    } catch (error) {
      console.error('âŒ Failed to fetch recent activity:', error);
      return { activities: [] };
    }
  },
};

// --- Other APIs ---
export const projectAPI = {
  getAll: async () => ({ projects: [] }),
  create: async (data) => data,
  update: async (id, data) => data,
  delete: async (id) => ({ success: true })
};

export const contactAPI = {
  getAll: async () => ({ contacts: [] }),
  getById: async (id) => ({}),
  updateStatus: async (id, status) => ({ success: true }),
  delete: async (id) => ({ success: true })
};

export const uploadAPI = {
  uploadImage: async (file) => ({
    url: `https://picsum.photos/800/600?random=${Date.now()}`,
    message: 'Mock upload successful'
  })
};

export default api;
