import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Blog API calls
export const blogAPI = {
  // Get all blogs
  getAllBlogs: () => api.get('/blogs'),
  
  // Get single blog by ID
  getBlogById: (id) => api.get(`/blogs/${id}`),
  
  // Create new blog
  createBlog: (blogData) => api.post('/blogs', blogData),
  
  // Update blog
  updateBlog: (id, blogData) => api.put(`/blogs/${id}`, blogData),
  
  // Delete blog
  deleteBlog: (id) => api.delete(`/blogs/${id}`),
  
  // Upload image
  uploadImage: (formData) => api.post('/upload', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  }),
};

// Mock data for development (remove when backend is ready)
const mockBlogs = [
  {
    id: 1,
    title: 'Getting Started with React Hooks',
    excerpt: 'Learn how to use React Hooks to simplify your functional components and manage state effectively.',
    content: 'Full blog content here...',
    image: null,
    icon: 'fas fa-code',
    date: '2024-03-15',
    readTime: '5 min read',
    author: 'Zaw Myo Oo',
    tags: ['React', 'JavaScript', 'Frontend'],
    published: true
  },
  {
    id: 2,
    title: 'Modern CSS Techniques',
    excerpt: 'Explore modern CSS features like Grid, Flexbox, and CSS Variables to create responsive layouts.',
    content: 'Full blog content here...',
    image: null,
    icon: 'fas fa-palette',
    date: '2024-03-12',
    readTime: '7 min read',
    author: 'Zaw Myo Oo',
    tags: ['CSS', 'Web Design', 'Frontend'],
    published: true
  }
];

// Mock functions for development
export const mockBlogAPI = {
  getAllBlogs: () => Promise.resolve({ data: mockBlogs }),
  getBlogById: (id) => Promise.resolve({ data: mockBlogs.find(blog => blog.id === parseInt(id)) }),
  createBlog: (blogData) => {
    const newBlog = {
      id: mockBlogs.length + 1,
      ...blogData,
      date: new Date().toISOString().split('T')[0],
      readTime: `${Math.ceil(blogData.content.length / 1000)} min read`,
      author: 'Zaw Myo Oo',
      published: true
    };
    mockBlogs.push(newBlog);
    return Promise.resolve({ data: newBlog });
  }
};

export default api;
