import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
    baseURL: API_BASE_URL,
});

// Request interceptor to add auth token
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('adminToken');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response interceptor to handle errors
api.interceptors.response.use(
    (response) => response,
    (error) => {
        if (error.response?.status === 401) {
            localStorage.removeItem('adminToken');
            localStorage.removeItem('adminUser');
            window.location.href = '/login';
        }
        return Promise.reject(error);
    }
);

// Admin Blog API
export const adminBlogAPI = {
    getAll: (page = 1, limit = 10) => api.get(`/blogs/admin/all?page=${page}&limit=${limit}`),
    create: (blogData) => {
        const formData = new FormData();
        Object.keys(blogData).forEach(key => {
            if (blogData[key] !== null && blogData[key] !== undefined) {
                formData.append(key, blogData[key]);
            }
        });
        return api.post('/blogs', formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });
    },
    update: (id, blogData) => {
        const formData = new FormData();
        Object.keys(blogData).forEach(key => {
            if (blogData[key] !== null && blogData[key] !== undefined) {
                formData.append(key, blogData[key]);
            }
        });
        return api.put(`/blogs/${id}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });
    },
    delete: (id) => api.delete(`/blogs/${id}`)
};

// Admin Project API
export const adminProjectAPI = {
    getAll: () => api.get('/projects'),
    create: (projectData) => {
        const formData = new FormData();
        Object.keys(projectData).forEach(key => {
            if (projectData[key] !== null && projectData[key] !== undefined) {
                formData.append(key, projectData[key]);
            }
        });
        return api.post('/projects', formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });
    },
    update: (id, projectData) => {
        const formData = new FormData();
        Object.keys(projectData).forEach(key => {
            if (projectData[key] !== null && projectData[key] !== undefined) {
                formData.append(key, projectData[key]);
            }
        });
        return api.put(`/projects/${id}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });
    },
    delete: (id) => api.delete(`/projects/${id}`)
};

// Auth API
export const authAPI = {
    login: (credentials) => api.post('/auth/login', credentials),
    register: (userData) => api.post('/auth/register', userData),
    getMe: () => api.get('/auth/me'),
    updateProfile: (userData) => api.put('/auth/profile', userData)
};

// Dashboard API
export const dashboardAPI = {
    getStats: () => api.get('/admin/dashboard/stats')
};

export default api;
