import React from 'react';

const Dashboard = ({ stats }) => {
    const statCards = [
        {
            icon: 'üìù',
            title: 'Total Blogs',
            number: stats.blogs.total,
            details: [
                { label: 'Published', value: stats.blogs.published, type: 'published' },
                { label: 'Draft', value: stats.blogs.draft, type: 'draft' }
            ]
        },
        {
            icon: 'üíº',
            title: 'Total Projects',
            number: stats.projects.total,
            details: [
                { label: 'Published', value: stats.projects.published, type: 'published' },
                { label: 'Draft', value: stats.projects.draft, type: 'draft' }
            ]
        },
        {
            icon: 'üìà',
            title: 'Total Content',
            number: stats.blogs.total + stats.projects.total,
            details: [
                { label: 'All Items', value: stats.blogs.total + stats.projects.total, type: 'published' }
            ]
        },
        {
            icon: 'üéØ',
            title: 'Active Content',
            number: stats.blogs.published + stats.projects.published,
            details: [
                { label: 'Live Items', value: stats.blogs.published + stats.projects.published, type: 'published' }
            ]
        }
    ];

    return (
        <div className="dashboard-widgets">
            <div className="stats-grid">
                {statCards.map((stat, index) => (
                    <div key={index} className="stat-card">
                        <div className="stat-card-header">
                            <div className="stat-icon">
                                {stat.icon}
                            </div>
                        </div>
                        <div className="stat-info">
                            <h3>{stat.title}</h3>
                            <div className="stat-number">{stat.number}</div>
                            <div className="stat-details">
                                {stat.details.map((detail, idx) => (
                                    <span key={idx} className={detail.type}>
                                        {detail.value} {detail.label}
                                    </span>
                                ))}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
