import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Blog API calls
export const blogAPI = {
  // Get all blogs
  getAllBlogs: () => api.get('/blogs'),
  
  // Get single blog by ID
  getBlogById: (id) => api.get(`/blogs/${id}`),
  
  // Create new blog
  createBlog: (blogData) => api.post('/blogs', blogData),
  
  // Update blog
  updateBlog: (id, blogData) => api.put(`/blogs/${id}`, blogData),
  
  // Delete blog
  deleteBlog: (id) => api.delete(`/blogs/${id}`),
};

// Mock data for development (remove when backend is ready)
const mockBlogs = [
  {
    id: 1,
    title: 'Getting Started with React Hooks',
    excerpt: 'Learn how to use React Hooks to simplify your functional components and manage state effectively.',
    content: 'React Hooks are a powerful feature that allows you to use state and other React features in functional components. They were introduced in React 16.8 and have since become the standard way to write React components.\n\nIn this blog post, we will explore the most commonly used hooks like useState, useEffect, and useContext. We will also learn how to create custom hooks to share logic between components.',
