    published: true
  }
];

// Mock functions for development
export const mockBlogAPI = {
  getAllBlogs: () => Promise.resolve({ data: mockBlogs }),
  getBlogById: (id) => Promise.resolve({ data: mockBlogs.find(blog => blog.id === parseInt(id)) }),
  createBlog: (blogData) => {
    const newBlog = {
      id: mockBlogs.length + 1,
      ...blogData,
      date: new Date().toISOString().split('T')[0],
      readTime: `${Math.ceil(blogData.content.length / 1000)} min read`,
      author: 'Zaw Myo Oo',
      published: true
    };
    mockBlogs.push(newBlog);
    return Promise.resolve({ data: newBlog });
  },
  updateBlog: (id, blogData) => {
    const index = mockBlogs.findIndex(blog => blog.id === parseInt(id));
    if (index !== -1) {
      mockBlogs[index] = { ...mockBlogs[index], ...blogData };
      return Promise.resolve({ data: mockBlogs[index] });
    }
    return Promise.reject(new Error('Blog not found'));
  },
  deleteBlog: (id) => {
    const index = mockBlogs.findIndex(blog => blog.id === parseInt(id));
    if (index !== -1) {
      mockBlogs.splice(index, 1);
      return Promise.resolve({ data: { message: 'Blog deleted successfully' } });
    }
    return Promise.reject(new Error('Blog not found'));
  }
};

export default api;
