import React, { useState } from 'react';
import axios from 'axios';

const BackendTester = () => {
  const [status, setStatus] = useState('idle');
  const [message, setMessage] = useState('');

  const testConnection = async () => {
    setStatus('testing');
    setMessage('Testing connection to backend...');
    
    const testUrls = [
      'http://localhost:5000/api/health',
      'http://127.0.0.1:5000/api/health',
      'http://192.168.100.62:5000/api/health'
    ];

    for (let url of testUrls) {
      try {
        setMessage(`Testing ${url}...`);
        const response = await axios.get(url, { timeout: 5000 });
        setStatus('connected');
        setMessage(`‚úÖ Backend found at: ${url}\nResponse: ${JSON.stringify(response.data)}`);
        return;
      } catch (error) {
        console.log(`‚ùå ${url} failed: ${error.message}`);
      }
    }
    
    setStatus('error');
    setMessage('‚ùå Could not connect to backend on any URL. Make sure backend is running on port 5000.');
  };

  return (
    <div style={{ 
      padding: '20px', 
      border: '1px solid #ccc', 
      borderRadius: '8px',
      margin: '10px 0',
      backgroundColor: status === 'connected' ? '#d4edda' : 
                     status === 'error' ? '#f8d7da' : 
                     status === 'testing' ? '#fff3cd' : '#f8f9fa'
    }}>
      <h3>üîß Backend Connection Test</h3>
      <button onClick={testConnection} disabled={status === 'testing'}>
        {status === 'testing' ? 'Testing...' : 'Test Backend Connection'}
      </button>
      
      {message && (
        <div style={{ 
          marginTop: '15px', 
          padding: '10px', 
          backgroundColor: 'white',
          borderRadius: '4px',
          border: '1px solid #dee2e6',
          whiteSpace: 'pre-wrap',
          fontFamily: 'monospace',
          fontSize: '14px'
        }}>
          {message}
        </div>
      )}

      {status === 'error' && (
        <div style={{ marginTop: '15px', padding: '10px', backgroundColor: '#f8d7da' }}>
          <h4>üö® Troubleshooting Steps:</h4>
          <ol>
            <li>Open terminal in backend folder</li>
            <li>Run: <code>npm start</code></li>
            <li>You should see: "Server successfully started on port 5000"</li>
            <li>Test in browser: <a href="http://localhost:5000/api/health" target="_blank">http://localhost:5000/api/health</a></li>
          </ol>
        </div>
      )}
    </div>
  );
};

export default BackendTester;
