import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import '../components/auth/Login.css';

export default function LoginPage() {
  const { login } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [networkStatus, setNetworkStatus] = useState('');

  // Check network connectivity before attempting login
  const checkNetwork = async () => {
    if (!navigator.onLine) {
      throw new Error('No internet connection. Please check your network.');
    }
    
    // Optional: Test if API server is reachable
    try {
      const testResponse = await fetch(`${process.env.REACT_APP_API_URL || ''}/health`, {
        method: 'GET',
        timeout: 5000
      });
      if (!testResponse.ok) {
        throw new Error('Server is not responding properly');
      }
    } catch (testError) {
      console.warn('Server health check failed:', testError);
      // Continue with login anyway, as health endpoint might not exist
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setNetworkStatus('');
    setLoading(true);

    try {
      // Validate inputs first
      if (!email || !password) {
        throw new Error('Please enter both email and password');
      }

      // Check network connectivity
      setNetworkStatus('Checking network...');
      await checkNetwork();

      // Attempt login with better user feedback
      setNetworkStatus('Connecting to server...');
      console.log('Attempting login...', { email });
      
      const data = await login(email, password);
      
      console.log('Login success:', data);
      setNetworkStatus('Login successful! Redirecting...');

      // Small delay to show success message
      setTimeout(() => {
        window.location.href = '/';
      }, 1000);

    } catch (err) {
      console.error('Login error:', err);
      
      // Enhanced error messages based on error type
      let errorMessage = 'Login failed. Please check your credentials.';
      
      if (err.message.includes('timeout') || err.message.includes('Timeout')) {
        errorMessage = 'Connection timeout. The server is taking too long to respond. Please try again in a moment.';
        setNetworkStatus('Server timeout - please try again');
      } else if (err.message.includes('network') || err.message.includes('Network')) {
        errorMessage = 'Network error. Please check your internet connection.';
        setNetworkStatus('Network connection issue');
      } else if (err.message.includes('credentials') || err.message.includes('401')) {
        errorMessage = 'Invalid email or password. Please try again.';
        setNetworkStatus('Authentication failed');
      } else {
        errorMessage = err.message || errorMessage;
        setNetworkStatus('Login failed');
      }
      
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Test credentials hint (remove in production)
  const fillTestCredentials = () => {
    setEmail('admin@example.com');
    setPassword('password123');
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Admin Login</h1>
        
        {/* Network status indicator */}
        {networkStatus && (
          <div className={`network-status ${error ? 'error' : ''}`}>
            {networkStatus}
          </div>
        )}
        
        {/* Error message */}
        {error && <div className="error-message">{error}</div>}
        
        {/* Debug helper - remove in production */}
        {process.env.NODE_ENV === 'development' && (
          <div className="debug-hint">
            <button 
              type="button" 
              className="debug-btn"
              onClick={fillTestCredentials}
            >
              Fill Test Credentials
            </button>
          </div>
        )}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              placeholder="Enter your password"
            />
          </div>

          <button 
            className={`login-btn ${loading ? 'loading' : ''}`} 
            type="submit" 
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Logging in...
              </>
            ) : (
              'Login'
            )}
          </button>
        </form>

        {/* Help text for users */}
        <div className="login-help">
          <p>If you continue experiencing issues:</p>
          <ul>
            <li>Check your internet connection</li>
            <li>Try again in a few moments</li>
            <li>Contact support if the problem persists</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
