import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';

const DebugLogin = () => {
  const [credentials, setCredentials] = useState({
    email: 'admin@example.com', // pre-filled for testing
    password: 'password'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { login } = useAuth();

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    console.log('Submitting login:', credentials);
    
    try {
      const result = await login(credentials);
      console.log('Login result:', result);
      
      if (result.success) {
        console.log('Login successful!');
        // Should redirect automatically via ProtectedRoute
      } else {
        setError(result.message || 'Login failed');
      }
    } catch (error) {
      console.error('Login catch error:', error);
      setError('Login error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const testCredentials = [
    { email: 'admin@example.com', password: 'password' },
    { email: 'test@test.com', password: 'test' },
    { email: 'admin@admin.com', password: 'admin' }
  ];

  const fillTestCredentials = (test) => {
    setCredentials(test);
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>Admin Login - Debug</h2>
        
        <div className="test-credentials">
          <h4>Test Credentials:</h4>
          {testCredentials.map((test, index) => (
            <button 
              key={index}
              type="button"
              onClick={() => fillTestCredentials(test)}
              className="test-btn"
            >
              Use: {test.email} / {test.password}
            </button>
          ))}
        </div>
        
        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}
        
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={credentials.email}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        
        <div className="debug-info">
          <p><strong>Current credentials:</strong></p>
          <p>Email: {credentials.email}</p>
          <p>Password: {credentials.password}</p>
        </div>
      </form>
    </div>
  );
};

export default DebugLogin;
