import React, { useState, useEffect } from 'react';
import { statsAPI, blogAPI, projectsAPI, contactAPI } from '../services/api';
import Dashboard from '../components/Dashboard';

const DashboardPage = () => {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    totalProjects: 0,
    totalMessages: 0,
    recentBlogs: [],
    recentMessages: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // You can create a dedicated stats endpoint or fetch individually
      const [blogsRes, projectsRes, contactsRes] = await Promise.all([
        blogAPI.getAll(),
        projectsAPI.getAll(),
        contactAPI.getAll()
      ]);

      setStats({
        totalBlogs: blogsRes.data.length,
        totalProjects: projectsRes.data.length,
        totalMessages: contactsRes.data.length,
        recentBlogs: blogsRes.data.slice(0, 5),
        recentMessages: contactsRes.data.slice(0, 5)
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-page">
      <Dashboard stats={stats} loading={loading} />
    </div>
  );
};

export default DashboardPage;
