const handleSubmit = async (e) => {
  e.preventDefault();
  setUploading(true);

  try {
    const submitData = new FormData();
    submitData.append('title', formData.title);
    submitData.append('content', formData.content);
    submitData.append('excerpt', formData.excerpt);
    submitData.append('category', formData.category);
    submitData.append('tags', JSON.stringify(formData.tags.split(',').map(tag => tag.trim())));
    submitData.append('published', formData.published.toString()); // ‚úÖ FIXED: Convert to string
    
    if (formData.image) {
      submitData.append('image', formData.image);
    } else if (formData.imageUrl) {
      submitData.append('imageUrl', formData.imageUrl);
    }

    // üêõ DEBUG: Log what we're sending
    console.log('üìù Blog data being sent:', {
      title: formData.title,
      published: formData.published,
      publishedString: formData.published.toString()
    });

    let result;
    if (blog) {
      result = await blogsAPI.update(blog._id, submitData);
    } else {
      result = await blogsAPI.create(submitData);
    }

    console.log('‚úÖ Blog saved successfully:', result);
    onSave(result.data);
  } catch (error) {
    console.error('‚ùå Error saving blog:', error);
  } finally {
    setUploading(false);
  }
};
