import React from 'react';
import { Link } from 'react-router-dom';

const RecentActivity = ({ blogs, projects }) => {
    const activities = [
        ...blogs.map(blog => ({
            type: 'blog',
            title: blog.title,
            status: blog.status,
            date: blog.createdAt,
            icon: 'ðŸ“',
            color: '#6366f1'
        })),
        ...projects.map(project => ({
            type: 'project',
            title: project.title,
            status: project.status,
            date: project.createdAt,
            icon: 'ðŸ’¼',
            color: '#8b5cf6'
        }))
    ].sort((a, b) => new Date(b.date) - new Date(a.date)).slice(0, 6);

    const getTimeAgo = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
        
        if (diffInHours < 1) return 'Just now';
        if (diffInHours < 24) return `${diffInHours}h ago`;
        if (diffInHours < 168) return `${Math.floor(diffInHours / 24)}d ago`;
        return `${Math.floor(diffInHours / 168)}w ago`;
    };

    return (
        <div className="recent-activity-card glass">
            <div className="card-header">
                <h3>Recent Activity</h3>
                <span className="card-subtitle">Latest updates and changes</span>
            </div>
            
            <div className="activity-list">
                {activities.length === 0 ? (
                    <div className="no-activity">
                        <div className="no-activity-icon">ðŸ“Š</div>
                        <p>No recent activity</p>
                        <small>Create some content to see activity here</small>
                    </div>
                ) : (
                    activities.map((activity, index) => (
                        <div key={index} className="activity-item">
                            <div 
                                className="activity-icon"
                                style={{ backgroundColor: `${activity.color}15`, color: activity.color }}
                            >
                                {activity.icon}
                            </div>
                            
                            <div className="activity-content">
                                <div className="activity-title">{activity.title}</div>
                                <div className="activity-meta">
                                    <span className={`activity-status ${activity.status}`}>
                                        {activity.status}
                                    </span>
                                    <span className="activity-type">{activity.type}</span>
                                </div>
                            </div>
                            
                            <div className="activity-time">
                                {getTimeAgo(activity.date)}
                            </div>
                            
                            <div className="activity-dot" style={{ backgroundColor: activity.color }}></div>
                        </div>
                    ))
                )}
            </div>
            
            {activities.length > 0 && (
                <div className="card-footer">
                    <Link to="/blogs" className="view-all-link">
                        View All Activity â†’
                    </Link>
                </div>
            )}
        </div>
    );
};

export default RecentActivity;
