import React, { useState } from 'react';
import { projectAPI } from '../services/api';

const ProjectManager = ({ onProjectUpdate }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    content: '',
    technologies: '',
    projectUrl: '',
    githubUrl: '',
    featuredImage: '',
    status: 'draft',
    category: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const imageFormData = new FormData();
    imageFormData.append('image', file);

    try {
      setLoading(true);
      const response = await projectAPI.uploadImage(imageFormData);
      setFormData(prev => ({
        ...prev,
        featuredImage: response.data.imageUrl
      }));
      setMessage('Image uploaded successfully!');
    } catch (error) {
      setMessage('Error uploading image: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const projectData = {
        ...formData,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech)
      };

      await projectAPI.createProject(projectData);
      setMessage('Project created successfully!');
      setFormData({
        title: '',
        description: '',
        content: '',
        technologies: '',
        projectUrl: '',
        githubUrl: '',
        featuredImage: '',
        status: 'draft',
        category: ''
      });
      
      if (onProjectUpdate) {
        onProjectUpdate();
      }
    } catch (error) {
      setMessage('Error creating project: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="project-manager">
      <h2>Create New Project</h2>
      
      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="project-form">
        <div className="form-group">
          <label htmlFor="title">Project Title *</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Short Description *</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
            required
            placeholder="Brief description of the project..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="content">Project Details</label>
          <textarea
            id="content"
            name="content"
            value={formData.content}
            onChange={handleChange}
            rows="8"
            placeholder="Detailed description, features, challenges, etc..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="technologies">Technologies Used</label>
          <input
            type="text"
            id="technologies"
            name="technologies"
            value={formData.technologies}
            onChange={handleChange}
            placeholder="React, Node.js, MongoDB, etc..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="projectUrl">Live Project URL</label>
          <input
            type="url"
            id="projectUrl"
            name="projectUrl"
            value={formData.projectUrl}
            onChange={handleChange}
            placeholder="https://your-project.com"
          />
        </div>

        <div className="form-group">
          <label htmlFor="githubUrl">GitHub Repository URL</label>
          <input
            type="url"
            id="githubUrl"
            name="githubUrl"
            value={formData.githubUrl}
            onChange={handleChange}
            placeholder="https://github.com/yourusername/project"
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Category</label>
          <input
            type="text"
            id="category"
            name="category"
            value={formData.category}
            onChange={handleChange}
            placeholder="Web App, Mobile App, API, etc..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="featuredImage">Featured Image</label>
          <input
            type="file"
            id="featuredImage"
            accept="image/*"
            onChange={handleImageUpload}
          />
          {formData.featuredImage && (
            <div className="image-preview">
              <img src={formData.featuredImage} alt="Preview" />
            </div>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            id="status"
            name="status"
            value={formData.status}
            onChange={handleChange}
          >
            <option value="draft">Draft</option>
            <option value="published">Published</option>
          </select>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Creating Project...' : 'Create Project'}
        </button>
      </form>
    </div>
  );
};

export default ProjectManager;
