import React, { useState, useEffect } from 'react';
import BlogManager from '../components/BlogManager';
import { blogsAPI } from '../services/api';

function BlogsPage() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const response = await blogsAPI.getAll();
      
      // ✅ FIX: Handle different response structures
      let blogsData = response;
      
      if (response && response.data) {
        blogsData = response.data; // If response has data property
      } else if (Array.isArray(response)) {
        blogsData = response; // If response is directly the array
      } else {
        blogsData = []; // Fallback to empty array
      }
      
      console.log('Blogs data:', blogsData); // Debug log
      setBlogs(Array.isArray(blogsData) ? blogsData : []);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      setBlogs([]); // Set to empty array on error
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (blogData) => {
    try {
      await blogsAPI.create(blogData);
      await fetchBlogs();
      setShowForm(false);
    } catch (error) {
      console.error('Error creating blog:', error);
    }
  };

  const handleUpdateBlog = async (blogData) => {
    try {
      await blogsAPI.update(blogData.id, blogData);
      await fetchBlogs();
      setEditingBlog(null);
    } catch (error) {
      console.error('Error updating blog:', error);
    }
  };

  const handleDeleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await blogsAPI.delete(id);
        await fetchBlogs();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  if (loading) {
    return (
      <div className="blogs-page">
        <div className="loading">Loading blogs...</div>
      </div>
    );
  }

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Manage Blog Posts</h1>
        <button 
          onClick={() => setShowForm(true)} 
          className="btn-primary"
        >
          Create New Blog
        </button>
      </div>

      {/* ✅ FIX: Add safe check for blogs array */}
      {blogs && blogs.length === 0 ? (
        <div className="no-blogs">
          <p>No blog posts found.</p>
          <p>Create your first blog post to get started!</p>
        </div>
      ) : (
        <div className="blogs-list">
          {/* ✅ FIX: Add safe mapping with proper keys */}
          {blogs.map(blog => (
            <div key={blog._id || blog.id} className="blog-item">
              <div className="blog-info">
                <h3>{blog.title}</h3>
                <p className="blog-excerpt">{blog.excerpt}</p>
                <div className="blog-meta">
                  <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                    {blog.published ? 'Published' : 'Draft'}
                  </span>
                  <span className="date">
                    {new Date(blog.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
              
              <div className="blog-actions">
                <button 
                  onClick={() => setEditingBlog(blog)}
                  className="btn-edit"
                >
                  Edit
                </button>
                <button 
                  onClick={() => handleDeleteBlog(blog._id || blog.id)}
                  className="btn-delete"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {showForm && (
        <BlogManager
          onSave={handleCreateBlog}
          onCancel={() => setShowForm(false)}
        />
      )}

      {editingBlog && (
        <BlogManager
          blog={editingBlog}
          onSave={handleUpdateBlog}
          onCancel={() => setEditingBlog(null)}
        />
      )}
    </div>
  );
}

export default BlogsPage;
