import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import '../components/auth/Login.css';

export default function LoginPage() {
  const { login } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [networkStatus, setNetworkStatus] = useState('');
  const [serverStatus, setServerStatus] = useState('unknown');

  const API_URL = process.env.REACT_APP_API_URL || 'https://zmo-backend.onrender.com/api';

  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    try {
      setNetworkStatus('Checking production server...');

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000);

      // Use the production health endpoint
      const response = await fetch(`${API_URL}/health`, {
        method: 'GET',
        signal: controller.signal,
        headers: {
          'Content-Type': 'application/json',
        },
      });

      clearTimeout(timeoutId);

      if (response.ok) {
        const healthData = await response.json();
        setServerStatus('running');
        setNetworkStatus('‚úÖ Production server is running');
        return true;
      } else {
        setServerStatus('error');
        setNetworkStatus('‚ö†Ô∏è Server error - check backend CORS configuration');
        return false;
      }
    } catch (error) {
      setServerStatus('down');

      if (error.name === 'AbortError') {
        setNetworkStatus('‚ùå Server timeout - check if backend allows CORS from localhost');
      } else if (error.message.includes('CORS')) {
        setNetworkStatus('‚ùå CORS blocked - backend needs to allow localhost:3000');
      } else {
        setNetworkStatus('‚ùå Cannot connect to production server');
      }

      return false;
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setNetworkStatus('');
    setLoading(true);

    try {
      if (!email || !password) {
        throw new Error('Please enter both email and password');
      }

      setNetworkStatus('Connecting to production server...');
      const isServerRunning = await checkServerStatus();

      if (!isServerRunning) {
        throw new Error('Cannot connect to production server. The backend is blocking requests from localhost due to CORS policy.');
      }

      setNetworkStatus('Attempting login...');
      console.log('Attempting login to production server:', { email, API_URL });

      const data = await login(email, password);

      console.log('Login success:', data);
      setNetworkStatus('‚úÖ Login successful! Redirecting...');

      setTimeout(() => {
        window.location.href = '/';
      }, 1000);

    } catch (err) {
      console.error('Login process error:', err);

      let errorMessage = 'Login failed. Please check your credentials.';
      let networkStatusMessage = 'Login failed';

      if (err.message.includes('CORS') || err.message.includes('cors')) {
        errorMessage = 'CORS Error: Backend is blocking requests from localhost. The backend needs to be updated to allow localhost:3000.';
        networkStatusMessage = 'üö´ CORS Blocked';
      } else if (err.message.includes('timeout')) {
        errorMessage = 'Server timeout. The backend might be having issues.';
        networkStatusMessage = '‚è∞ Timeout';
      } else if (err.message.includes('credentials') || err.message.includes('401')) {
        errorMessage = 'Invalid email or password. Please try again.';
        networkStatusMessage = 'üîê Auth Failed';
      } else {
        errorMessage = err.message || errorMessage;
      }

      setError(errorMessage);
      setNetworkStatus(networkStatusMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Admin Login</h1>

        {/* Server info */}
        <div className="server-info">
          <p><strong>Backend:</strong> {API_URL}</p>
          <div className={`server-status ${serverStatus}`}>
            Production Server: 
            {serverStatus === 'running' && ' ‚úÖ Connected'}
            {serverStatus === 'down' && ' ‚ùå CORS Issue'}
            {serverStatus === 'error' && ' ‚ö†Ô∏è Error'}
            {serverStatus === 'unknown' && ' üîÑ Checking...'}
          </div>
        </div>

        {networkStatus && (
          <div className={`network-status ${error ? 'error' : ''}`}>
            {networkStatus}
          </div>
        )}

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
              placeholder="admin@example.com"
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              placeholder="Enter password"
            />
          </div>

          <button 
            className={`login-btn ${loading ? 'loading' : ''}`} 
            type="submit" 
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Connecting...
              </>
            ) : (
              'Login to Production'
            )}
          </button>
        </form>

        {/* CORS troubleshooting guide */}
        <div className="troubleshooting">
          <h3>üö´ CORS Issue Detected</h3>
          <p><strong>Problem:</strong> Backend is blocking requests from localhost:3000</p>
          <p><strong>Solution:</strong> Update backend CORS configuration to allow:</p>
          <ul>
            <li><code>http://localhost:3000</code></li>
            <li><code>http://localhost:3001</code></li>
            <li><code>http://127.0.0.1:3000</code></li>
          </ul>
          <p><strong>Backend URL:</strong> <code>https://zmo-backend.onrender.com</code></p>
          <p><strong>Render Dashboard:</strong> <a href="https://render.com/dashboard" target="_blank" rel="noopener noreferrer">https://render.com/dashboard</a></p>
        </div>
      </div>
    </div>
  );
}
