import axios from 'axios';

// API Configuration
const API_BASE_URL = 'https://zmo-backend.onrender.com/api';

// Create axios instance with better error handling
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000, // 30 seconds
  withCredentials: false,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log('üöÄ Making API request to:', config.url);
    return config;
  },
  (error) => {
    console.error('‚ùå Request setup error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor with better error handling
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API Response success:', response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error('‚ùå API Response error:', {
      url: error.config?.url,
      message: error.message,
      code: error.code,
      status: error.response?.status
    });

    // Enhanced error messages
    if (error.code === 'ECONNABORTED') {
      error.message = 'Request timeout - server is taking too long to respond';
    } else if (error.message === 'Network Error') {
      error.message = 'Cannot connect to server. Please check your internet connection and ensure the backend is running.';
    } else if (error.response?.status === 404) {
      error.message = 'API endpoint not found';
    } else if (error.response?.status >= 500) {
      error.message = 'Server error - please try again later';
    }

    return Promise.reject(error);
  }
);

// API methods
export const login = async (email, password) => {
  try {
    console.log('üîê Attempting login for:', email);
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  } catch (error) {
    console.error('üí• Login API error:', error);
    throw new Error(error.message || 'Login failed. Please try again.');
  }
};

export const verifyToken = async (token) => {
  try {
    const response = await api.get('/auth/verify', {
      headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
  } catch (error) {
    throw new Error('Token verification failed');
  }
};

// Test connection function
export const testConnection = async () => {
  try {
    console.log('üîó Testing connection to backend...');
    const response = await api.get('/health');
    console.log('‚úÖ Backend connection successful:', response.data);
    return { success: true, data: response.data };
  } catch (error) {
    console.error('‚ùå Backend connection failed:', error.message);
    return { success: false, error: error.message };
  }
};

export default api;
