import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    image: project?.image || '',
    featured: project?.featured || false
  });

  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(project?.image || '');
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
        setFormData(prev => ({ ...prev, image: e.target.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setImageFile(null);
    setImagePreview('');
    setFormData(prev => ({ ...prev, image: '' }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Simulate upload progress
    setUploadProgress(0);
    const progressInterval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 90) {
          clearInterval(progressInterval);
          return 90;
        }
        return prev + 10;
      });
    }, 100);

    try {
      // âœ… CORRECT: Prepare data for backend (removed URL fields)
      const projectData = {
        name: formData.name,
        description: formData.description,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: formData.image, // This will be the base64 string or existing URL
        featured: formData.featured
      };
      
      // If editing, include the _id
      if (project?._id) {
        projectData._id = project._id;
      }

      // If there's a new image file, you might want to handle upload here
      if (imageFile) {
        console.log('ðŸ“¸ New image file to upload:', imageFile.name);
        // You can upload the image to your server here and get back a URL
        // For now, we're using the base64 string directly
      }

      setUploadProgress(100);
      
      console.log('ðŸ“¦ Sending project data to backend:', projectData);
      
      // Wait a bit to show 100% progress
      setTimeout(() => {
        onSave(projectData);
        setUploadProgress(0);
      }, 500);

    } catch (error) {
      console.error('Error uploading project:', error);
      setUploadProgress(0);
      clearInterval(progressInterval);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal project-manager-modal modern-modal">
        <div className="modal-header">
          <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
          <button 
            type="button" 
            className="close-btn" 
            onClick={onCancel}
            disabled={uploadProgress > 0}
          >
            <i className="fas fa-times"></i>
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="modern-form">
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">
                Project Name <span className="required">*</span>
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                placeholder="Enter project name"
                className="form-input"
                required
              />
            </div>

            <div className="form-group full-width">
              <label className="form-label">
                Description <span className="required">*</span>
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                placeholder="Enter project description, features, and your role..."
                className="form-textarea"
                required
                rows="4"
              />
            </div>

            <div className="form-group full-width">
              <label className="form-label">
                Technologies Used <span className="required">*</span>
              </label>
              <input
                type="text"
                value={formData.technologies}
                onChange={(e) => setFormData({...formData, technologies: e.target.value})}
                placeholder="React, Node.js, MongoDB (comma separated)"
                className="form-input"
                required
              />
              <div className="input-hint">
                Separate technologies with commas
              </div>
            </div>

            {/* Image Upload Section */}
            <div className="form-group full-width">
              <label className="form-label">Project Image</label>
              
              {imagePreview ? (
                <div className="image-preview-modern">
                  <div className="preview-card">
                    <img src={imagePreview} alt="Project preview" className="preview-image" />
                    <div className="preview-actions">
                      <button 
                        type="button" 
                        className="action-btn danger"
                        onClick={removeImage}
                      >
                        <i className="fas fa-trash"></i>
                        Remove
                      </button>
                      <label className="action-btn secondary">
                        <i className="fas fa-edit"></i>
                        Change
                        <input
                          type="file"
                          accept="image/*"
                          onChange={handleImageChange}
                          className="hidden-input"
                        />
                      </label>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="upload-card">
                  <input
                    type="file"
                    id="project-image"
                    accept="image/*"
                    onChange={handleImageChange}
                    className="hidden-input"
                  />
                  <label htmlFor="project-image" className="upload-area">
                    <div className="upload-icon">
                      <i className="fas fa-cloud-upload-alt"></i>
                    </div>
                    <div className="upload-text">
                      <p className="upload-title">Click to upload project image</p>
                      <p className="upload-subtitle">PNG, JPG, WEBP up to 5MB</p>
                    </div>
                  </label>
                </div>
              )}
            </div>

            {/* Fallback URL input for existing images */}
            {!imagePreview && !imageFile && (
              <div className="form-group full-width">
                <label className="form-label">Or enter image URL</label>
                <input
                  type="url"
                  value={formData.image}
                  onChange={(e) => setFormData({...formData, image: e.target.value})}
                  placeholder="https://example.com/project-image.jpg"
                  className="form-input"
                />
              </div>
            )}

            <div className="form-group full-width">
              <div className="featured-toggle">
                <label className="toggle-label">
                  <div className="toggle-content">
                    <span className="toggle-text">Featured Project</span>
                    <span className="toggle-hint">
                      Featured projects will be highlighted on your portfolio
                    </span>
                  </div>
                  <div className="toggle-switch">
                    <input
                      type="checkbox"
                      checked={formData.featured}
                      onChange={(e) => setFormData({...formData, featured: e.target.checked})}
                      className="toggle-input"
                    />
                    <span className="slider"></span>
                  </div>
                </label>
              </div>
            </div>
          </div>

          {/* Upload Progress */}
          {uploadProgress > 0 && (
            <div className="upload-progress-modern">
              <div className="progress-header">
                <span className="progress-title">
                  {project ? 'Updating Project...' : 'Creating Project...'}
                </span>
                <span className="progress-percent">{uploadProgress}%</span>
              </div>
              <div className="progress-bar-modern">
                <div 
                  className="progress-fill-modern" 
                  style={{ width: `${uploadProgress}%` }}
                ></div>
              </div>
            </div>
          )}

          <div className="modal-actions">
            <button type="button" className="btn btn-outline" onClick={onCancel} disabled={uploadProgress > 0}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary" disabled={uploadProgress > 0}>
              {uploadProgress > 0 ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i>
                  {project ? 'Updating...' : 'Creating...'}
                </>
              ) : (
                <>
                  <i className="fas fa-save"></i>
                  {project ? 'Update Project' : 'Create Project'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
