import axios from 'axios';

// ==========================================
// üöÄ ENVIRONMENT CONFIGURATION
// ==========================================
const ENVIRONMENT = process.env.REACT_APP_ENV || 'production';
const APP_VERSION = process.env.REACT_APP_VERSION || '2.0.0';
const API_TIMEOUT = parseInt(process.env.REACT_APP_API_TIMEOUT) || 30000; // 30s

// Use production URL from env, fallback to localhost only in dev
const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL ||
  (ENVIRONMENT === 'development' ? 'http://localhost:5000/api' : 'https://zmo-backend.onrender.com/api');

console.log('üîß ZMO Admin Panel API Config:', { API_BASE_URL, ENVIRONMENT, APP_VERSION, API_TIMEOUT });

// ==========================================
// üõ†Ô∏è AXIOS INSTANCE (UPDATED - REMOVED X-REQUEST-ID)
// ==========================================
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: API_TIMEOUT,
  headers: {
    'Content-Type': 'application/json',
    'X-Client': 'zmo-admin-panel',
    'X-Client-Version': APP_VERSION,
    'X-Environment': ENVIRONMENT,
  },
  withCredentials: false,
});

// ==========================================
// üîÑ REQUEST INTERCEPTOR (UPDATED - REMOVED X-REQUEST-ID)
// ==========================================
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) config.headers.Authorization = `Bearer ${token}`;
    
    // ‚úÖ REMOVED: config.headers['X-Request-ID'] = generateRequestId();
    // This was causing the CORS error
    
    console.log(`üöÄ API Request: ${config.method?.toUpperCase()} ${config.url}`, { hasToken: !!token });
    return config;
  },
  (error) => Promise.reject(error)
);

// ==========================================
// üì° RESPONSE INTERCEPTOR
// ==========================================
api.interceptors.response.use(
  (response) => response,
  (error) => {
    handleApiError(error);
    return Promise.reject(error);
  }
);

// ==========================================
// üõ°Ô∏è ERROR HANDLER
// ==========================================
const handleApiError = (error) => {
  const status = error.response?.status;

  switch (status) {
    case 401:
      clearAuthData();
      redirectToLogin();
      break;
    case 403:
      showToast('You do not have permission to perform this action', 'error');
      break;
    case 404:
      console.log('üîç API endpoint not found');
      break;
    case 429:
      showToast('Too many requests, try later', 'warning');
      break;
    case 500:
      showToast('Server error, try later', 'error');
      break;
    case 502:
    case 503:
    case 504:
      showToast('Backend temporarily unavailable', 'error');
      break;
    default:
      if (error.code === 'TIMEOUT') showToast('Request timeout', 'warning');
      else if (error.message?.includes('CORS')) {
        console.error('‚ùå CORS Error - Request blocked by security policy');
        showToast('Connection blocked by security policy. Please try again.', 'error');
      }
      else if (error.message === 'Network Error') {
        console.error('‚ùå Network Error - Cannot connect to server');
        showToast('Cannot connect to server. Please check if the backend is running.', 'error');
      }
      break;
  }
};

// ==========================================
// üîê AUTH UTILITIES
// ==========================================
const getToken = () => localStorage.getItem('adminToken') || sessionStorage.getItem('adminToken');
const setToken = (token, rememberMe = false) =>
  rememberMe ? localStorage.setItem('adminToken', token) : sessionStorage.setItem('adminToken', token);
const clearAuthData = () => {
  localStorage.removeItem('adminToken');
  localStorage.removeItem('adminUser');
  sessionStorage.removeItem('adminToken');
  sessionStorage.removeItem('adminUser');
};
const redirectToLogin = () => {
  if (!window.location.pathname.includes('/login')) {
    setTimeout(() => (window.location.href = `/login?redirect=${encodeURIComponent(window.location.pathname)}&session=expired`), 1500);
  }
};

// ==========================================
// üõ†Ô∏è UTILS (UPDATED - REMOVED GENERATE REQUEST ID)
// ==========================================
// ‚úÖ REMOVED: const generateRequestId = () => `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

const showToast = (message, type = 'info') => {
  console.log(`üì¢ ${type.toUpperCase()}: ${message}`);
  if (typeof window !== 'undefined')
    window.dispatchEvent(new CustomEvent('showToast', { detail: { message, type, duration: 5000 } }));
};

// ==========================================
// üîê AUTH API
// ==========================================
export const authAPI = {
  login: async (email, password, rememberMe = false) => {
    try {
      console.log('üîê Attempting login to:', API_BASE_URL);
      
      const response = await api.post('/auth/login', {
        email: email.trim().toLowerCase(),
        password: password.trim(),
      });

      const { success, token, user, message } = response.data;

      if (success && token) {
        setToken(token, rememberMe);
        localStorage.setItem('adminUser', JSON.stringify(user));
        console.log('‚úÖ Login successful:', { user: user.email, token: token.substring(0, 20) + '...' });
        return { success: true, user, token, message };
      } else {
        console.error('‚ùå Login failed:', message || 'Unknown error');
        throw new Error(message || 'Login failed');
      }
    } catch (error) {
      console.error('‚ùå Login API error:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      
      let errorMessage = 'An error occurred during login. Please try again.';
      
      if (error.message === 'Network Error') {
        errorMessage = 'Cannot connect to the server. Please check if the backend is running.';
      } else if (error.message.includes('CORS')) {
        errorMessage = 'Connection blocked by security policy. Please try again.';
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      }
      
      throw new Error(errorMessage);
    }
  },
  
  logout: async () => {
    try {
      await api.post('/auth/logout');
    } catch (error) {
      console.log('‚ö†Ô∏è Backend logout failed (this is normal if backend is down):', error.message);
    } finally {
      clearAuthData();
      return { success: true, message: 'Logout successful' };
    }
  },
  
  verify: async () => {
    const response = await api.get('/auth/verify');
    if (response.data.success) return response.data;
    else {
      clearAuthData();
      throw new Error('Token verification failed');
    }
  },
  
  getCurrentUser: () => {
    try {
      return JSON.parse(localStorage.getItem('adminUser'));
    } catch {
      return null;
    }
  },
  
  isAuthenticated: () => !!getToken(),
};

// ==========================================
// üìä DASHBOARD API
// ==========================================
export const dashboardAPI = {
  getStats: async () => {
    const response = await api.get('/admin/dashboard/stats');
    if (response.data.success) return response.data;
    else throw new Error('Failed to fetch dashboard stats');
  },
};

// ==========================================
// üìù BLOG API
// ==========================================
export const blogAPI = {
  getAll: async (params = {}) => {
    const response = await api.get('/admin/blogs', { params });
    const blogs = response.data.success && response.data.data?.blogs ? response.data.data.blogs : [];
    return { success: true, blogs, total: blogs.length, pagination: response.data?.pagination };
  },
};

// ==========================================
// üîó HEALTH CHECK
// ==========================================
export const testBackendConnection = async () => {
  try {
    console.log('üîó Testing backend connection...', API_BASE_URL);
    const response = await api.get('/health');
    console.log('‚úÖ Backend connection successful:', response.data);
    return { success: true, data: response.data, message: 'Backend is healthy' };
  } catch (error) {
    console.error('‚ùå Backend connection failed:', {
      message: error.message,
      code: error.code,
      url: API_BASE_URL
    });
    
    let errorMessage = 'Cannot connect to backend';
    if (error.message === 'Network Error') {
      errorMessage = 'Cannot connect to backend server. The server might be down.';
    } else if (error.message.includes('CORS')) {
      errorMessage = 'Connection blocked by CORS policy.';
    }
    
    return { 
      success: false, 
      error: error.message, 
      message: errorMessage 
    };
  }
};

// Auto-test connection in development
if (ENVIRONMENT === 'development') {
  setTimeout(testBackendConnection, 1000);
}

export { clearAuthData, getToken };
export default api;
